INSERT INTO `gift` (`id`, `name`, `addr`, `feature`, `picurl`, `city`, `town`, `lat`, `lng`) VALUES
(1, '銅鑼杭菊美人茶', '苗栗縣銅鑼鄉永樂路22號', '苗栗縣銅鑼地區於民國50年代開始種植杭菊，由中藥材變農村觀光休閒。今花茶系列飲品加入東方美人茶，沖泡後更有意想不到之風味。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151026161106.jpg', '苗栗縣', '銅鑼鄉', 24.487964, 120.788222),
(2, '曬幸福-阿婆的手造品', '苗栗縣三義鄉龍騰村外庄10號', '每年冬季芥菜豐收時，客家庄的婦女展現醃晒好手藝，將芥菜製成酸菜、福菜、梅乾菜保存，並製作各具風味的佳餚，芥菜含有豐富的胡蘿蔔素、維生素B群、維生素C，及鐵等營養素，是值得讓您回味起客家阿婆的好味道。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151026161215.jpg', '苗栗縣', '三義鄉', 24.3892633, 120.769475),
(3, '柴之燒炭焙-龍眼乾禮盒', '南投縣中寮鄉永平村永平路186號', '選用水份最少的粉殼品種龍眼，以龍眼樹枝為柴薪，由經驗老道的師傅遵循古法，以乾柴慢火、煙燻如霧，經歷 3 天 2 夜， 一遍又一遍的耐心烘焙，果肉金黃帶點炭焙香，Q、甜無雜味，風味獨具，無論當零嘴或入菜，是行家的最愛。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151026163803.jpg', '南投縣', '中寮鄉', 23.8787397, 120.767809),
(4, '台灣原生種土-肉桂手工皂', '南投縣中寮鄉永平村永平路186號', '以傳統冷製法製皂，保留植物油中對肌膚的滋養，讓皂的氣味達到簡單、舒服、溫暖，更加入肉桂精油、純露及肉桂粉添入皂方，達到深層潔淨肌膚。從繳皂、入模、允模、切皂到等待至完成期，每一道過程都為每一塊肥皂嚴格把關，或許這樣會增加成本及時間，但這麼做卻能為我們的地球保留好的生態環境以及給我們的肌膚多一點健康。\r\n\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151026163842.jpg', '南投縣', '中寮鄉', 23.8787397, 120.767809),
(5, '喜諾巴萊-小米醇酒', '南投縣仁愛鄉南豐村中正路80-3號', '在賽德克的穀倉之鄉，黃金粟成熟時，七月某夜我們做了Mgaya tasmgasuc，各家要於小米收割前先做年糕、醃肉、魚肉、及釀酒。於清晨攜帶米糕到小米田上，用松樹香樹皮點燃火把，以迎接火神。割小米田的麥穗兩把，持於手中以黃金粟米酒「喜諾巴萊」含口嘖之，讓生命的靈糧通向聖靈世界。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151026172014.jpg', '南投縣', '仁愛鄉', 24.004799, 121.088911),
(6, '夢谷咖啡禮盒', '南投縣仁愛鄉南豐村中正路80-3號', '種植於海拔1,100～1,300公尺的「阿拉比卡」品種咖啡，提升栽培管理與烘焙技術，並以手工去皮、去果肉後採日照自然乾造法產出生豆，因而所焙製出咖啡除少了泡水去果肉過程中發酵產生之酸味外，呈現出的是高海拔特有濃郁香醇、微帶自然酸味與飲後回甘的特色。以主產地「夢谷」為品牌並結合在地賽德克原住民之工藝文化為產品包裝特色，希望能將別具風味與在地產業文化特色的咖啡饗予國人共享。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151026172056.jpg', '南投縣', '仁愛鄉', 24.004799, 121.088911),
(7, '蔥小子系列零嘴', '宜蘭縣三星鄉中山路 31 號', '青蔥在三星地區之栽培已有二十餘年的歷史，青蔥蘊含硫化丙烯，可提高維生素B1的作用，搭配適當食品調理，極具保健成效，人言「鼎底若有蔥，無肉也會香」，其美味更是眾所皆知。\r\n經三星鄉農會積極開發後，已有深具獨特性的青蔥醬、青蔥粉、蔥油、青蔥奶油醬、蔥花調理包等多種調味供大眾選擇，烹調應用於拌麵、炒飯、入菜、燒肉、沾醬等樣樣皆宜，讓做菜輕鬆又愉快，並能品嚐另類青蔥好味道，另有蔥明麵、蔥明燒、蔥小子蛋捲及蔥小子派等麵食零嘴提供消費者，物美價廉、自用送禮兩相宜，是三星鄉特有之伴手禮品。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151023170055.jpg', '宜蘭縣', '三星鄉', 24.6679321, 121.652284),
(8, '凍橄梅梅', '南投縣水里鄉民生路362號', '每到了秋天，11月至來年3月進入橄欖成熟期，接著又是採梅季。一想到橄欖吃在嘴裏那種回甘及梅子那股酸勁就口水直流。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151027094908.jpg', '南投縣', '水里鄉', 23.8171797, 120.8541),
(9, '水里真梅', '南投縣水里鄉民生路362號', '水里會於九十一年推出梅品，命名為『水里真梅』以建立品牌與競爭力，並嚐嚐當地特產的梅子製品，期能分享梅品新風格與好滋味。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151027095134.jpg', '南投縣', '水里鄉', 23.8171797, 120.8541),
(10, '日日青春曲-蜂蜜梅精果日日青春曲─蜂蜜梅精果', '南投縣水里鄉民生路362號', '當梅精遇上蜂蜜展現青春、活力與健康，就像一首台灣流傳的老歌-日日春。', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151027095339.jpg', '南投縣', '水里鄉', 23.8171797, 120.8541),
(11, '四珍凍頂茶禮盒', '南投縣鹿谷鄉初鄉村中正路三段293號', '本社成立於1974年2月，主要業務生產及共同運銷，成立宗旨確保純正凍頂茶品質與信譽，生產總面積2000餘公頃，主要市場以內銷為主，外銷為輔。凍頂烏龍茶取自於福建武夷山之茶區，1855年由本鄉林鳳池先生高中清朝舉人後帶回贈親人，命名為凍頂烏龍茶。\r\n凍頂烏龍茶之茶種為青心烏龍茶種，以首功採摘幼嫩茶菁，年採4至5次，屬輕發酵茶，外觀緊結成半球狀，色澤墨綠，水色蜜黃澄清明亮圓滑富活性，回甘強，是香氣滋味並重之珍貴茶葉。', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151027110331.jpg', '南投縣', '鹿谷鄉', 23.7643532, 120.735456),
(12, '三星上將梨果醬', '宜蘭縣三星鄉中山路 31 號', '嚴選三星上將梨以新鮮檸檬熬煮，加入少許鳳梨提味，無任何添加物，讓您吃得美味又健康。', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151023170919.jpg', '宜蘭縣', '三星鄉', 24.6679321, 121.652284),
(13, '三金-凍頂茶禮盒', '南投縣鹿谷鄉初鄉村中正路三段293號', '茶葉為南投縣最具代表性之農產，鹿谷鄉茶葉面積約2,000餘公頃，年產量1,600公噸，以既有的茶葉文化和歷史背景為後盾,推出三金-凍頂產銷履歷茶禮盒，值得尊貴的您細細品嚐。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151027110428.jpg', '南投縣', '鹿谷鄉', 23.7643532, 120.735456),
(14, '茶鄉米香', '南投縣竹山鎮下橫街38號', '精選杉林溪好茶與竹山濁水溪米，再利用竹炭過濾出純淨甘甜好水，讓茶湯更鮮明甘甜，讓米飯更吮指回香，用好水、好茶、好米，帶來健康、無負擔的每一天！\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151027110510.jpg', '南投縣', '竹山鎮', 23.756698, 120.68463),
(15, '大地果實幸福禮盒', '南投縣竹山鎮下橫街38號', '運用最健康、最天然的大地果實-竹山紅番薯、紫蜜番薯、地瓜葉所製成的Q甜手工地瓜麵，搭配西式餅皮，採用地瓜泥為餡烘焙出口感密實鬆軟地瓜燒，香甜綿密滿滿幸福好滋味。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151027110553.jpg', '南投縣', '竹山鎮', 23.756698, 120.68463),
(16, '半路店梅子味噌禮盒', '南投縣信義鄉明德村新開巷 11 號', '傳統製法是將大豆煮熟攪捽搓揉後，加入海鹽調味，搓勻後放入米麴或麥麴，最後放入木桶內封存發酵，味噌成熟後便可得到又香又濃的口味。 烹調味噌主要以味噌湯的方式食用，此外在燉湯、蒸魚、肉、蔬菜時加入梅子味噌拌和調味料，能使湯、菜的味道更鮮美，經常食用有利於身體健康。據說日本人的長壽就與經常食用味噌有關。味噌中含有較多的蛋白質、脂肪、醣類以及鐵、鈣、鋅、維生素B1、B2和尼克酸等營養物質精選米、黃豆、梅汁、鹽混合製作而成，不添加防腐劑、色素等有害添加物質。試試看 梅子味噌 真的很棒 …\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151027111847.jpg', '南投縣', '信義鄉', 23.688701, 120.861791),
(17, '信義風情禮盒組', '南投縣信義鄉明德村新開巷 11 號', '忘記回家－開懷梅露，又稱勇士的血液，因為它像獵槍一般陪伴勇士接受時間磨練及大自然的洗禮和體力的考驗與狩獵完成後族人的讚詠與榮耀。高品質黃熟梅果混合上等砂糖存封一年，再經昇華取其精華之50度部份結合高品質黃熟梅果加入上等冰糖及高質米酒糖經半年存封而成調和而成，造就出25度高質開懷梅露。\r\n\r\n長老說話─橡木桶梅露-信義鄉農會頂級梅露之一，以進口橡木桶存封經長時間存封於恆溫環境之下，再裝瓶上市，口感甜滑香醇，且少了市面上一般梅露的燥味，非常適合開懷暢飲。', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151027112141.jpg', '南投縣', '信義鄉', 23.688701, 120.861791),
(18, '歡囍幸福婚宴酒禮盒', '南投縣信義鄉明德村新開巷 11 號', '是南投縣信義鄉農會嚴選在地優質青梅釀造而成的梅酒，溫潤的口感與酸甜討喜的滋味，常讓人忽略其酒精濃度達 12。\r\n【農業易遊網提醒您：飲酒過量有礙身體健康，喝酒不開車】\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151027112308.jpg', '南投縣', '信義鄉', 23.688701, 120.861791),
(19, '愛旦-台灣梅禮盒組愛旦台灣梅禮盒組', '南投縣信義鄉明德村新開巷 11 號', '原料採用台灣南投縣信義鄉在地青梅，製作傳統梅粉與烏梅粉；牛軋條風味獨具，是茶飲聚會零嘴最佳伴侶，經濟實惠送禮大方。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151027112906.jpg', '南投縣', '信義鄉', 23.688701, 120.861791),
(20, '台灣農夫禮盒', '南投縣魚池鄉東池村福興巷13-12號', '讓蔬菜、水果在嘴裡唱歌跳舞，享受美味無負擔的天然享受！台灣農夫禮盒包括了日月潭紅茶、酥果脆餅、香蕈脆餅。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151027113057.jpg', '南投縣', '魚池鄉', 23.8976522, 120.942481),
(21, '日月潭幸福飲', '南投縣魚池鄉東池村福興巷13-12號', '融合了日月潭紅玉紅茶與自然花草，經由細心的烘焙，呈現了芬芳的嗅覺美感。\r\n幸福飲，傳遞了真實的情意，和真心的祝福。\r\n花草茶寧靜的氣質，給予您一段幸福的好時光。', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151027113145.jpg', '南投縣', '魚池鄉', 23.8976522, 120.942481),
(22, '南投茶宴禮盒', '南投縣南投市文昌街 45 號', '南投縣農會為保障消費者權益，維護產銷合理化，委請茶葉品評專家鑑定。', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151027113228.jpg', '南投縣', '南投市', 23.9073655, 120.687753),
(23, '南投茶經禮盒', '南投縣南投市文昌街 45 號', '台灣最大的茶葉生產區─南投縣，邀請您品嚐傳遞南投八大茶區最珍貴的茶禪文化。', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151027113338.jpg', '南投縣', '南投市', 23.9073655, 120.687753),
(24, '南投梅宴禮盒-梅好時刻', '南投縣南投市文昌街 45 號', '南投以產梅聞名，集合各地區的特色農產精心製成多款風味梅，內含南投市甜菊梅、 鹿谷鄉烏龍茶梅、信義鄉原味梅、水里鄉紫蘇梅、埔里鎮玫瑰花梅、中寮鄉肉桂梅，品嚐時猶如暢遊南投，一次擁有最美好的酸甜滋味。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151027113430.jpg', '南投縣', '南投市', 23.9073655, 120.687753),
(25, '南投梅宴-黃梅酥', '南投縣南投市文昌街 45 號', '梅子是最佳鹼性食品，富函鈣、鎂、鐵、鉀等鹼基礦物質，有助於體內之酸鹼平衡，梅子更富含天然有機酸，可生津解渴、減低疲勞促進新陳代謝，經常食用梅子相關產品有易維持健康。本廠利用現有CAS優良蜜餞加工廠，將黃熟梅採收洗選晾乾水分，糖漬後去籽絞碎成泥製程鮮美甘甜之鹼性餡料，再結合麵粉等必需材料製成健康、可口美味之黃梅酥，其酸甜滋味有別於當下鳳梨酥風味，食用後不甜不膩的感覺，讓人能口齒留香、回味無窮，呈獻給消費者耳目一新的印象，為餽贈親友最佳選擇之伴手禮。', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151027113505.jpg', '南投縣', '南投市', 23.9073655, 120.687753),
(26, '南投冰工廠', '南投縣南投市文昌街 45 號', '採用南投縣在地特產作物為原料，真材實料，可品嚐到真實果粒及獨特香氣，強調無色素、無防腐劑、無香精，純手工製作，保留古早風味，讓消費者可以吃得安心吃得健康。（有梅、茶葉、百香果、玫瑰花、荔枝、鳳梨、柳橙等7種口味）\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151027113557.jpg', '南投縣', '南投市', 23.9073655, 120.687753),
(27, '花點心呷好醋禮盒', '南投縣埔里鎮清新里西安路一段6號', '栽種於花都—埔里，國產的食用玫瑰花令人驚艷。玫瑰花醋、玫瑰花瓣醬是花點心系列的要角，我們相信生活中只要多花點心、就可以營許多幸福和浪漫氛圍，埔里鎮農會花點心做好醋，也期待您呷好醋時要記得花點心～～\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151027143911.jpg', '南投縣', '埔里鎮', 23.963721, 120.964167),
(28, '美人腿湯麵', '南投縣埔里鎮清新里西安路一段6號', '茭白筍是埔里的農特產，以潔白無晳的外形博得「美人腿」之譽，透過特殊冷凍乾燥技術，保留原有營養成分，沖泡後口感鮮嫩爽脆，每人腿泡麵共推出牛肉、肉燥及素食等三種口味，方便又營養。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151027144128.jpg', '南投縣', '埔里鎮', 23.963721, 120.964167),
(29, '花．心 下午茶組花．心午茶組', '南投縣埔里鎮清新里西安路一段6號', '選用可食用山形玫瑰，馥郁極佳品種，嚴製不添加任何人工香料的玫瑰花瓣醬，再搭配玫瑰手工餅乾，邀您沉浸在浪漫下午茶時光。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151027144436.jpg', '南投縣', '埔里鎮', 23.963721, 120.964167),
(30, '錦歡喜禮盒', '南投縣草屯鎮碧山路190號', '禮盒基本配搭以草屯鎮聞名全國的：錦早米、合歡米為主，再搭配養生並有情投意合寓意的薏仁、傳統習俗「呷米香，嫁好尪」的爆米香、象徵早生貴子、圓圓滿滿的桂圓、讓人吃了嘴甜心更甜的喜糖、喜餅等，取其主力產品名稱和其它喜氣洋洋的產品，將禮盒命名為：錦歡喜訂婚米禮盒，並取其台語諧音有「真歡喜」的寓意，將新嫁娘歡喜的心情分享給親朋好友們。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151027144520.jpg', '南投縣', '草屯鎮', 23.978165, 120.682201),
(31, '吉荔糕', '南投縣草屯鎮碧山路190號', '使用台稉九號米穀粉做成香酥可口、無負擔的外皮，內餡則採用楊貴妃的最愛糯米荔枝來入料，咬一口後，滿嘴散發出濃濃的荔枝香，那種香而不豔、甜而不膩的滋味，如同唐明皇與楊貴妃的愛情，令人迴盪不已。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151027144559.jpg', '南投縣', '草屯鎮', 23.978165, 120.682201),
(32, '五結好禮-稻米禮盒', '宜蘭縣五結鄉五結村五結中路23號', '適地適種優質米「夢田越光米」，米粒晶瑩剔透，口感及Q度均比一般米還要來的高，是目前全世界高品質稻米的代表。五結鄉農會推薦越光米伴手禮盒、女兒禮、吉米果子，以精緻五結好禮與您結緣。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151023171325.jpg', '宜蘭縣', '五結鄉', 24.6855829, 121.797628),
(33, '菇鄉香酥禮盒', '南投縣魚池鄉魚池街 439 號', '來自優質的香菇栽培場，嚴選外貌佳、顆粒較大的香菇，是頂級的爽脆咬勁。本產品無添加任何化學物質，保證新鮮、自然、美味，更是貼心的伴手禮。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151027144641.jpg', '南投縣', '魚池鄉', 23.8961886, 120.935039),
(34, '日月潭典藏茶包', '南投縣魚池鄉魚池街 439 號', '魚池農會典藏禮盒以「農委會茶業改良場魚池分廠製茶工廠」的紅茶傳奇；時間軸從畫面左方的台灣紅茶之父─新井耕吉郎以貓囒山常見樹種，象徵921重生力量的橄欖樹為中心，有點出回顧與展望的心情。日月潭典藏茶包，包裝採用原色牛皮紙兼具美觀及環保，舊有包裝為裝禮盒包裝，體積大攜帶不便且成本高，消費者購買意願低，經改變包裝後為3包裝禮盒，型態體積減少60包裝成本減少20將可提高消費者購買意願。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151027144725.jpg', '南投縣', '魚池鄉', 23.8961886, 120.935039),
(35, '菇菇茶集禮盒', '南投縣魚池鄉魚池街 439 號', '南投魚池鄉有三寶，為香菇、紅茶、蘭花草，特別集合當地最具特色之優質農產品，精心研發多款點心。忙碌的午後，來塊香脆爽口的紅茶餅與原味菇脆片，搭配一杯溫熱的阿薩姆或是紅玉紅茶，輕鬆擁有愜意午茶時光。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151027144825.jpg', '南投縣', '魚池鄉', 23.8961886, 120.935039),
(36, '日月潭茶之捲禮盒', '南投縣魚池鄉魚池街 439 號', '由阿薩姆茶末製成紅茶蛋捲，慢慢咀嚼，淡淡的茶香迴盪在唇齒之間，再搭配淡淡麥芽香的阿薩姆紅茶，最大的享受莫過於此。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151027144911.jpg', '南投縣', '魚池鄉', 23.8961886, 120.935039),
(37, '貴妃枇杷、宋媽媽葡萄', '南投縣國姓鄉昌榮巷 55-12 號', '農場內枇杷甜度都在12度以上，食後具有清甜的風味。每年葡萄產期有兩季，口感Q、香氣濃厚、甜度都在19~20左右。此外葡萄園以人工除草，不使用除草劑。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151027145410.jpg', '南投縣', '國姓鄉', 23.9959569, 120.851505),
(38, '幸福雪酪', '南投縣名間鄉田仔村田仔巷16-8號', '將紫山藥與鮮奶結合，堅持不加一滴水、不加香精及色素，製成美味兼具食療的雪酪，天然、健康、養生，讓您吃到百分百天然在地的幸福味，宛如紫山藥藤蔓纏綿般，觸動溫暖人心。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151027145455.jpg', '南投縣', '名間鄉', 23.865896, 120.674956),
(39, '柚香米酥', '彰化縣二水鄉聖化村員集路三段678號', '取自充滿陽光八卦山脈所栽種的頂極大白柚果皮肉，用純麥芽糖以古老方式熬煮而成，再搭配在地二水好米製成的營養米榖粉的酥皮，低糖、低油、低熱量是最佳的旅遊伴手。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151027160402.jpg', '彰化縣', '二水鄉', 23.810138, 120.616152),
(40, '柚惑魔棒柚香米棒', '彰化縣二水鄉聖化村員集路三段678號', '嚴選特產白柚及臨濁水溪盛產濁水米的「柚香米棒」，有淡淡的柚香，帶著微甜而不膩的口感，用良心來製作、讓消費者買得開心，吃得安心。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151027160631.jpg', '彰化縣', '二水鄉', 23.810138, 120.616152),
(41, '手工日曬蕎麥麵', '彰化縣二林鎮南光里儒林路二段260號', '麵麵俱到  長長久久\r\n近年來二林農會極力推廣二林三寶其中一二寶蕎麥與薏仁，研發出頗具特色的麵條，不但深受大眾喜愛，近來更將蕎麥麵與紅薏仁麵融入伴手女兒禮部分，以木盒為包材，點綴著代表富貴的牡丹花圖騰，讓女兒禮的心意更表不凡，期待在地的農產精品能讓大眾所接受，也讓女兒禮有更多樣化的選擇。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151027160727.jpg', '彰化縣', '二林鎮', 23.8961344, 120.369817),
(42, '大城土豆麥酥', '彰化縣大城鄉東城村南平路256號', '傳承大城鄉的老農夫不服輸的精神的「土豆麥酥」，取自大城鄉最好的土豆，最健康的小麥絕對讓您吃出「不會輸」的感覺!\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151027161038.jpg', '彰化縣', '大城鄉', 23.853374, 120.321886),
(43, '美春姐古早味雞精阿春姐古早味雞精', '彰化縣芬園鄉彰南路五段451巷36號', '一隻成熟的古早雞，歷經十二個小時隔水慢火精煉，完全不加一滴水，利用高溫將整隻雞之精華一滴一滴的逼出。用心的經過去油、過濾、去渣等多道工序，讓純淨的營養，不油不膩。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151027161123.jpg', '彰化縣', '芬園鄉', 24.0371467, 120.621062),
(44, '阿春姐荔枝酥餅', '彰化縣芬園鄉彰南路五段451巷36號', '以農場著名產品荔枝乾作為酥餅原料，棗紅色的內餡飽滿厚實，融合荔枝特有的果香及餅皮的奶香，整體味道非常豐富。小巧造型一口恰恰好，讓人越吃越順口。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151027161214.jpg', '彰化縣', '芬園鄉', 24.0371467, 120.621062),
(45, '蘭花精緻小禮盆', '彰化縣大村鄉中山路三段137號', '各式蘭花精緻小禮盒，自用送禮皆可。', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151027161309.jpg', '彰化縣', '大村鄉', 23.9968785, 120.558709),
(46, '米樂禮禮盒', '彰化縣田中鎮南北街80號', '在青青的田中央看見了微笑的稻子，農友以快樂用心的專業精神，細心灌溉，將最好的都給上，因此也種出了會微笑的稻子，那麼田中農會用無比歡欣的態度，從農友手中接到快樂的稻子，將之傳送與人們與消費者，讓禾樂禮成為快樂的傳播者。\r\n台梗九號米：用清純的濁水溪灌溉，用富有黑色黏質的壞土栽培，每一粒米都身懷飽滿，口感香Q，還有在田中這片青青土地所沾染的幸福滋味。\r\n牛奶手打麵：與田中乳牛產銷班合作依循曝晒日照方式，手工製造，麵條Q彈，每一口都有酪農的心意。\r\n古早味麵線：純天然、古報曝晒乾燥製成，純手作，獨門甩、拉、彈巧勁運用，口感嚼勁十足，百分之百原味天然，給你帶點陽光般的新好滋味。 \r\n胡麻油：遵循古法釀造，回歸自然，香濃淳、營養成分高，是健康調味、烹調時的最佳幫手。', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151027161548.jpg', '彰化縣', '田中鎮', 23.8596621, 120.586382),
(47, '穀意禮', '彰化縣田中鎮南北街80號', '台稉九號米：用清純的濁水溪灌溉，用富有黑色黏質的壤土栽培，如同自己的孩子般細心照料呵護，每一粒米都身懷飽滿香Ｑ口感。還有，在田中這片青青土地所沾染的幸福滋味，濁水溪流載了農家生活與土地情感，黃土沙泥涵養了田中生命悸動，豐沃了對故鄉風味的無窮思念！\r\n玄米醋：100純發酵釀造有益人體新陳代謝。嚴選「台灣米倉」濁水溪平源、八卦山脈穀物，田中稻米產銷專區台稉九號米，秉持傳統古法釀造，二年以上多重靜置發酵，酵素豐富無添加任何香料及化學合成品，入口回甘。', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151027161920.jpg', '彰化縣', '田中鎮', 23.8596621, 120.586382),
(48, '柚子蜜茶', '彰化縣二水鄉惠民村山腳路一段2號', '成分:柚子、柚子果粒、蜂蜜、砂糖 \r\n食用方法:以1：10之熱水或冷水攪勻即可 \r\n常溫：請置於乾燥、陰涼通風處，不宜曝曬陽光。開封後請至於冰箱內冷藏。純手工製作，完全不含人工添加物及防腐劑，是最天然又營養的自然食品，對人體健康又無負擔。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151027162336.jpg', '彰化縣', '二水鄉', 23.8077098, 120.631006),
(49, '富貴良緣禮盒', '彰化縣社頭鄉社斗路一段360號', '堅持遵循古法以龍眼木材烘焙，注重產品品質及衛生，包裝前再經機器烘乾殺菌，保留天然香 Q 風味及煙燻香氣，是自古流傳的養生珍品，另搭配香醇潤口的天然龍眼蜂蜜，不僅健康滋補，更使整個養生禮品呈現出濃濃的傳統風味。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151027162803.jpg', '彰化縣', '社頭鄉', 23.896825, 120.584708),
(50, '八樂鮮果禮盒', '彰化縣社頭鄉社斗路一段360號', '社頭鄉農會長期輔導鄉內果農，加入吉園圃生鮮蔬果產銷班，以自然農法栽種安全、高品質的芭樂，每年並舉辦芭樂品質競賽，鼓勵果農栽種高優質的鮮果，歷年參加全國芭樂評鑑皆獲團體第一名，社頭農會評選出外型、品質、甜度均優異的鮮果，以八顆為單位組成『芭樂鮮果禮盒』，將嚴選果品做差異化行銷，提升果農的收益。社頭鄉是台灣織襪產業的重鎮有襪子王國之稱，所生產的各種襪子，近年來多元發展賀開發，舉辦『織襪芭樂節』活動，吸引了大批的遊客參加，規劃與農會的家政班利用社所產的織襪製作目前受年輕人喜愛的襪子娃娃『芭樂織襪公仔娃娃』。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151027163017.jpg', '彰化縣', '社頭鄉', 23.896825, 120.584708),
(51, '美荔三寶', '彰化縣芬園鄉彰南路五段451巷36號', '選用的荔枝食材都是一顆顆新鮮現採手工剝殼、去籽、秤重、然後再進行不同製程工法，「荔枝乾」傳統柴窯燒烘培而成，甜美果粒碩大，口感Q又軟；「荔枝養生茶」搭配黑糖及老薑滋補，慢火熬煮方式，濃郁芳香之口感外，更將「荔枝、紅棗」顆粒完全保留下來，真材實料；「荔枝醋」有淡淡荔枝香，酸甜適中的口感，純天然無添加。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151027163315.jpg', '彰化縣', '芬園鄉', 24.0371467, 120.621062),
(52, '美春姐-龍情荔意', '彰化縣芬園鄉彰南路五段451巷36號', '採用八卦台東麓種植的荔枝及龍眼，品質優、甜度高，採用薑及麻油調味，彰化芬圓美春姐龍情荔意是尚天然擱甘甜ㄟ人情味。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151027172228.jpg', '彰化縣', '芬園鄉', 24.0371467, 120.621062),
(53, '龍花吉祥', '彰化縣鹿港鎮建國路7號', '為鹿港鎮農會養蜂產銷班所採擷精純甜蜜的濃情滋味，並通過全國養蜂協會驗證機制的100純正台灣蜂蜜，香甜而不膩的好風味，邀您一起來品嚐。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151027172358.jpg', '彰化縣', '鹿港鎮', 24.0650184, 120.4366),
(54, '芭樂玄米茶', '彰化縣田中鎮興工路509號', '精選芭樂小果、一花二葉的嫩葉，搭配擁有產銷履歷驗證的台梗九號米製成風味特殊的芭樂玄米茶。茶汁初入口，盡生芭樂清香；飲下入喉，深刻感受玄米獨有的烘炒米味。可重複沖泡，浸泡越久越甘醇。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151027172500.jpg', '彰化縣', '田中鎮', 23.8566985, 120.591207),
(55, '陽光芭樂果乾陽光芭樂乾', '彰化縣田中鎮興工路509號', '採用｢草生栽培｣法的鮮甜芭樂，手工製作，經過冰鎮、冷藏的方式處理，口感特別Q甜、有嚼勁，絕無添加色素、香精、防腐劑、甜味劑，為天然的果乾。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151027172545.jpg', '彰化縣', '田中鎮', 23.8566985, 120.591207),
(56, '蜂蜜桂圓茶', '彰化縣二水鄉聖化村員集路三段678號', '特選二水鄉古法柴燒烘乾之桂圓，與和信蜂業的純淨優質蜂蜜調和而成。入喉後清香甘甜，還可舒緩疲勞，溫暖一整天的疲累身心!\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151027172851.jpg', '彰化縣', '二水鄉', 23.810138, 120.616152),
(57, '風之鄉系列產品-黃金番薯燒-抹燒', '彰化縣線西鄉德興村德興路1號', '皮薄肉厚,Q度.軟度均佳,且不含防腐劑,拆封即可食用,冷藏後更香Q,以烤箱或微波加熱後,蕃薯的香味散了開來,加上天然海藻糖及黑芝麻提味,口感甜而不膩,在冷颼颼的冬天裡,來個蕃薯燒,熱呼呼.香噴噴的口感,讓人齒頰留香.\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151027173142.jpg', '彰化縣', '線西鄉', 24.128633, 120.479966),
(58, '溪州黑鑽紫黑米溪州紫黑米系列-貢米禮盒', '彰化縣溪州鄉溪州村中央路三段324號', '本紫黑米產自彰化縣溪州鄉濁水溪沿岸，泥土含有多種礦物元素，無農藥無化學肥料，是特別栽種的人氣產品，可天天食用，香氣逼人、好食又美味。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151027173315.jpg', '彰化縣', '溪州鄉', 23.8515366, 120.494963),
(59, '禾樂幸福米禮盒', '台中市大甲區中山路一段372號', '在大甲區幸福里專區農夫鋤耘的細心栽種之台稉九號良質米，以「鯉魚潭」之優質清澈水質灌溉，幸福滋育而成，為贈送親友最珍貴祝福—食有米飯、禾樂幸福。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151027173414.jpg', '臺中市', '大甲區', 24.334949, 120.615858),
(60, '大安好酒禮盒', '台中市大安區中庄里中松路 91 號', '九降風白蘭地-萃取金香葡萄所釀製的，釀製後做二次蒸餾，再經由橡木桶內熟成，吸收橡木桶獨特沉穩熟香。口感之濃郁、柔滑順喉、芳香甘冽不可言喻，層次豐富，後勁十足痛快，久久難忘。\r\n竽燒酎-沿襲日本燒酌製作流程，再依芋頭的的特性加以改良，並運用酒本身的溫度，將天然芋頭香氣封存於酒中。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151028094245.jpg', '臺中市', '大安區', 24.3493938, 120.589175),
(61, '棗茶兒-金棗濃糖果汁', '宜蘭縣冬山鄉梅山路168號', '「棗茶兒」為阿來伯系列產品第一彈，百分之百使用宜蘭在地生產「金棗」，歡迎來香格里拉農場與阿來伯一起追逐夢想。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151023180703.jpg', '宜蘭縣', '冬山鄉', 24.632506, 121.729009),
(62, '建國百年頭等倉米寶島雙星伴手禮', '台中市西區向上路1段95號', '本會所生產頭等倉米是採用台梗九號（蓬萊品種），使用第一頂級好米，還有台灣造型保鮮盒，可當收納盒或微波盒是多功能用途。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151028094428.jpg', '臺中市', '西區', 24.1465595, 120.663284),
(63, '幸福穗稻伴手米禮盒頭等倉-幸福穗稻伴手米禮盒', '台中市西區向上路1段95號', '嚴選台灣中部頂級好米-越光米，用心栽培，粒粒飽滿，邀您細嚐滿滿的幸福味。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151028094539.jpg', '臺中市', '西區', 24.1465595, 120.663284),
(64, '咱的后里', '台中市后里區民生路268號', '特選來自純淨雪山山脈大安溪水灌溉的白米與糙米，米質優良，兼顧健康與美味。並運用法國葡萄酒發酵技術，不添加任何香料，發揮葡萄自然果香及風味，口感香甜，果味怡人。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151028094623.jpg', '臺中市', '后里區', 24.3066984, 120.712628),
(65, '稻囍', '台中市后里區民生路268號', '出嫁女兒心是歡喜、是不捨，準備好農夫用心栽培的好米、傳統手工本喜氣大餅、帶點酸澀的紅酒，為您許上濃濃的祝福，女兒一定要幸福喔!\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151028094706.jpg', '臺中市', '后里區', 24.3066984, 120.712628),
(66, '好神祈福米', '台中市后里區民生路268號', '台灣各大廟宇讓朝拜的人們購拿祈福米的文化存在已久，此祈福米禮盒整理出大眾所期盼訴求包含：福、祿、壽、媽祖(平安)、菩薩(善緣)、註生娘娘(求子)、財神(求財)、月下老人(姻緣)、文昌帝君(學業、功祿)等九位神祉，讓大眾對自己或在乎的人的給祝福與期盼。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151028094910.jpg', '臺中市', '后里區', 24.3066984, 120.712628),
(67, '心有所薯', '台中市后里區民生路268號', '后里得天獨厚的地理環境，所生產的馬鈴薯品質優良，口感極佳，心有所薯薯條是非油炸食品，食用起來安心又健康。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151028095028.jpg', '臺中市', '后里區', 24.3066984, 120.712628),
(68, '黃金活力地瓜餐包', '台中市沙鹿區沙田路131號', '黃金活力地瓜餐包是使用沙鹿品質優良的紅土地瓜，香甜又鬆軟，搭配14種蔬菜穀物，便利、高纖、口感滑順，只要沖入熱開水，輕輕攪拌，即可享用，沖調鮮奶或牛奶，更為香純美味。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151028113413.jpg', '臺中市', '沙鹿區', 24.236049, 120.558948),
(69, '韭讚の麵', '台中市清水區中山路94號', '清水區農會精心製作的「韭讚麵」是將韭黄及韭菜打汁後經過濾直接和入麵粉，讓麵條有天然食物纖維，吃起來健康且更具韭香，Q彈爽口，幸福入味。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151028113935.jpg', '臺中市', '清水區', 24.2668384, 120.574757),
(70, '杏鮑菇系列產品菇饌的美味', '台中市新社區中和街四段226號', '菇類的纖維含量甚至是尋常蔬菜的好幾倍，除了低熱量的特點外，菇類還有蛋白質、維生素、礦物質、微量元素及多種胺基酸、多醣體等營養成分。是調和人體健康、符合養生概念的絕佳食材。產地：台灣．台中市。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151028114201.jpg', '臺中市', '新社區', 24.2436761, 120.808227),
(71, '菇賞禮盒', '台中市新社區中和街四段226號', '香菇採收後馬上嚴選分級，經過24小時70℃烘乾後，封存濃郁菇香，再分裝成家庭號「小菇意」；鮮菇爭鮮加工成香菇酥或香菇蘇打餅，讓人得以品味菇香、滿足味蕾。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151028114412.jpg', '臺中市', '新社區', 24.2436761, 120.808227),
(72, '將軍花翎百年紀念酒', '台中市霧峰區中正村四德路 10 號', '用臺灣最好的霧峰香米、最甘甜的埔里礦泉和日本嚴選麴菌，釀造全國唯一台式頂級清酒。讓每個人在微醺飄然中，看見霧峰鄉的壯闊山林，感受文化經典的優雅華美。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151028115407.jpg', '臺中市', '霧峰區', 24.0442951, 120.734995),
(73, '峰田小町', '台中市霧峰區中正村四德路 10 號', '霧峰境內產區花木扶疏，蝶舞翩然，是蜜蜂採蜜的美好環境，孕育出良好的蜂蜜品質，八種口味，呈獻不同的甜蜜體驗特別介紹給您。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151028115542.jpg', '臺中市', '霧峰區', 24.0442951, 120.734995),
(74, '台農鮮奶酪', '台中市外埔區水美里二崁路700號', '台農鮮奶酪以新鮮生乳煮沸殺菌製作而成，且因未加防腐劑，採定時定量供應，來到本牧場一定要嚐鮮一下!\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151028115701.jpg', '臺中市', '外埔區', 24.3258733, 120.626962),
(75, '香草醋系列香草系列產品', '雲林縣土庫鎮新建路6-5號', '西洋香草經過多年的推廣，已經逐漸成為國人健康與休閒生活的一部分。雲林土庫鎮農會香草產銷第一班藉由產學合作研發各種香草產品，在市場上廣受好評，其中又以，「奇香妙草─香草醋系列」最受到消費者的青睞。\r\n香草醋是將香草透過蘋果發酵醋的萃取香味，再包上蜂蜜的甜美，調配出薰衣草、玫瑰、檸檬草、迷迭香、紫蘇等多種口味的香草醋，稀釋飲用，冷熱皆宜，不但風味絕佳，更有調理身體平衡，增加活力的功能。「奇香妙草─香草醋系列」產品包裝精美，送禮也十分大方。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151028115756.jpg', '雲林縣', '土庫鎮', 23.6751521, 120.387665),
(76, '咖啡戀禮盒', '雲林縣古坑鄉永光村光昌12號', '古坑鄉位於雲林縣東方，鄉內民風純樸，以麻竹筍為大宗產品，近年來漸以柳丁，柑橘、香蕉、葡萄柚、茂谷柑、鳳梨等果樹為發展重點，另外，加比山咖啡更是近年來台灣咖啡的代名詞及旅人們的伴手禮的好選擇。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151028120059.jpg', '雲林縣', '古坑鄉', 23.624105, 120.55524),
(77, '好米伴名醬禮盒', '雲林縣西螺鎮延平路353號', '以台農71號益全香米為原料，從選育稻種、田間管理、安全用藥、專業收割、分級加工運輸、分級包裝、單一產區稻作品牌化行銷，從根本做出高品質的產品。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151028133246.jpg', '雲林縣', '西螺鎮', 23.8003737, 120.45463),
(78, '鵝樂-鵝翅、鵝腳', '雲林縣東勢鄉東南村民生路56號', '台灣人天性質樸溫暖，熱情愛分享，特別是吃到用心的美味，一定要宣傳。而這樣的讚美稱道，閩南語音為「鵝樂」，並有兒樂之美。早期農業社會小朋友穿著開襠庫和鵝玩耍被鵝追逐之樂趣，找回兒時農村記憶。一份有台灣味的思情伴手禮。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151028133332.jpg', '雲林縣', '東勢鄉', 23.675807, 120.253822),
(79, '東勢鵝肉美食系列產品', '雲林縣東勢鄉東南村民生路56號', '東勢鄉養鵝規模居全台之冠，博得「肉鵝王國」美譽之稱，以當地自產鵝，研發出鵝肉香腸、貢丸，不僅具有新鮮感及話題性，又以新鮮鵝肝，研發本土鵝肝醬，適合加工的鵝肝少之又少，更能顯現出鵝肝醬的珍貴。鵝肉香腸不油不膩，嚐過的民眾都成老主顧。符合食品衛生安全，東勢鄉伴手禮相當注意產品包裝標識。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151028133824.jpg', '雲林縣', '東勢鄉', 23.675807, 120.253822),
(80, '食在愛餡-蔥米捲', '宜蘭縣頭城鎮更新路125號', '名產古棗茶、黃金桔、土豆糖、蔥米捲一次購買，以「遊農場品名產」的概念，在包裝上呈現導覽圖搭配特製的「骰子」，使可邊吃邊玩！並附贈精美明細片。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151023180904.jpg', '宜蘭縣', '頭城鎮', 24.9071472, 121.849467),
(81, '莿桐說的蒜-蒜泥厲害', '雲林縣莿桐鄉中正路54號', '莿桐的黑葉丫蒜頭，香辣夠味，莿桐蒜醬更是麵包的好佐料，健康、安全，只在莿桐說的蒜。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151028134216.jpg', '雲林縣', '莿桐鄉', 23.7569732, 120.502638),
(82, '嘿讚幸運餅', '雲林縣北港鎮中正路20號', '『嘿讚幸運餅』取自北港在地的「黑金剛土豆」，種皮富含花青素營養滿分，拜土豆健康又長壽，送『嘿讚』事業桃花按個『讚』。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151028134301.jpg', '雲林縣', '北港鎮', 23.5688563, 120.304049),
(83, '杏鮑菇脆片', '嘉義縣東石鄉港墘村60號', '候鳥，季季來來回回，終在千里之外的東石尋得最佳棲息地，度過每個冷冽的寒冬。杏鮑菇生長在高溼氣山區，94年東石鄉農會，改建原有的雜糧廢棄倉庫，成為環控式低溫及RO逆滲透的培養環境，量身訂做一個屬於杏鮑菇的家。東石是候鳥避寒的家，也是杏鮑菇成長的家，一個最幸福的所在。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151028134742.jpg', '嘉義縣', '東石鄉', 23.459957, 120.212323),
(84, '杏福菇菇片', '嘉義縣東石鄉港墘村60號', '東石不只有夕陽漁人碼頭，還有「杏福寶寶」，東石杏鮑菇在細心的照顧下，各個品質優良、美味可口！一起搭上杏福小船往幸福出發囉！\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151028134844.jpg', '嘉義縣', '東石鄉', 23.459957, 120.212323),
(85, '柴燒土窯福圓', '台南市東山區南勢里一鄰6-2號', '52公頃的仙湖農場中心是座獨立山頭，因為晨間、黃昏及雨後的雲海，讓山頭看起來像座湖中小島，仙湖六代堅持古法製作的龍眼乾，從烘焙灶寮到生產步驟都充滿了古早味，歡迎旅人來到，品嚐這美味及美好。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151028145647.jpg', '臺南市', '東山區', 24.778289, 120.988108),
(86, '牧草茶飲伴手禮-四喜牧草茶飲', '台南市新營區民治路5號', '刺五加:為寒帶有氧植物屬五加科,俗名西伯利亞人參,自古以來為常用藥材,味辛、微苦、性溫。\r\n魚腥草:為三白草科,多年生草木,鮮葉具腥臭味,晒乾後有淡草香,是原住民生活中不可或缺的藥草之ㄧ。\r\n紅刺蔥:屬高地草本植物,因枝幹長滿銳利,又名鳥不踏,具強烈香氣,全株皆有利用價值,漢人又稱『食茱萸』。\r\n五葉蔘:屬葫蘆科,學名絞股藍,又名土葉膽,由於與人蔘皂甘結構相同,固有「南方人蔘」的美譽,葉味甘甜,不宜長期大量飲用。\r\n\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151028145748.jpg', '臺南市', '新營區', 23.3108819, 120.320843),
(87, '台灣鯛魚鬆、魚脯台灣鯛-魚鬆、魚脯禮盒', '台南市新營區民治路5號', '台南市水產養殖大都以虱目魚、鯛魚為主，台灣鯛魚除鮮食外，其加工品更以香酥甜甜魚酥、魚脯為品中之絕，遵古法口味加工製造成魚酥.魚脯且價格低廉，香、鬆、軟、化，魚韻十足，南瀛甜香特別甄選為代表性魚特產品。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151028150036.jpg', '臺南市', '新營區', 23.3108819, 120.320843),
(88, '玉井烘焙情人果', '台南市玉井區中正路139號', '印度人都在芒果樹下求婚，所以芒果就又稱「情人果」，在台南的山裡有一個小鎮叫玉井，它是芒果的故鄉，很多年前種下了一百顆的芒果，故事就這樣開始了。 堅持以最天然、新鮮芒果製作，不含防腐劑與色素，玉井情人果傳遞溫暖的的手作幸福。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151028150309.jpg', '臺南市', '玉井區', 23.1768678, 120.485224),
(89, '酸酸甜甜-約定手禮', '台南市玉井區中正路139號', '嚴選新鮮愛文芒果及土芒果果肉製作，不添加色素與防腐劑，多道低溫烘焙，封存最溫潤的口感，呈現食材原有的細緻風味。酸酸甜甜的好滋味，令人回味無窮，是傳達濃情蜜意的最佳伴手禮。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151028150353.jpg', '臺南市', '玉井區', 23.1768678, 120.485224),
(90, '回味玉井', '台南市玉井區中正路139號', '玉井好山、好水的自然環境條件，加上充足陽光與特殊氣候與緯度和芒果達人的經驗傳承，讓玉井芒果集聚『香、濃、Q、甜、美』風味於一身，令人回味與懷念的滋味，讓玉井擁有聞名中外「芒果之故鄉」美譽。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151028150429.bmp', '臺南市', '玉井區', 23.1768678, 120.485224),
(91, '玉井鮮芒果多千層蛋糕熱情小子-芒果多千層', '台南市玉井區中正路139號', '新鮮香濃味美在地愛文芒果，融合香草甜點與層層岩燒蛋皮，濃滑奶香、果香在嘴裡交融得恰到好處，迴盪舌尖，層次豐富味美濃郁，猶如跳著華麗圓舞曲甜蜜戀人，令人陶醉其中，是仲夏季節最令人心動幸福美食與必選清涼有勁的甜蜜伴手。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151028150513.jpg', '臺南市', '玉井區', 23.1768678, 120.485224),
(92, '龜山朝日-金棗酒', '宜蘭縣頭城鎮更新路126-50號', '龜山朝日滴滴金醇~純釀為8度酒露，色澤如曙光般金燦滴滴，純醇清新，蜜果香口感也宛如龜山島長期給予宜蘭子民心靈歸宿般甘醇。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151023181133.jpg', '宜蘭縣', '頭城鎮', 24.9036307, 121.862429),
(93, '國產優質胡麻油', '台南市安定區安定里58號', '台南市安定區於日治時代名為直加弄，後改為安定社，台灣光復後更名為安定區，追溯到古曾文溪逐年河道移位，年年河水氾濫，大水自山上沖刷肥沃土壤，淤積到下游河段。\r\n安定區正處於曾文溪畔，年第年年有沃土覆蓋，適合種植胡麻，產量大品質好，利潤較其他作物佳，農民皆以自產自銷的型態發展沿襲至今。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151028150705.jpg', '臺南市', '安定區', 23.1115672, 120.254157),
(94, '芝麻喜事禮盒', '台南市西港區文化路 2 號', '取自在地胡麻煉製，「胡麻香」品質純味美；「芝麻糖」甜甜，增添福氣好運氣，是喜事彌月送禮最佳選擇。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151028150807.jpg', '臺南市', '西港區', 23.12337, 120.204536),
(95, '玉井之門水果乾系列', '台南市南化區小崙里57-3號', '「玉井之門」創立於民國八十九年，創辦人本著客家人淳樸務實的精神，以夫家玉井為名，聯合了台南玉井地區50位農民所創立，玉井的特產為始，讓農民苦心栽種的水果，除了以新鮮水果的方式販售外，還能有另一種不同的品嚐方式，以最簡單無防腐添加物的加工方式，保有水果的原味，製作成各類水果乾，讓大家嚐到不同的美味，以期將產地的農特產推薦給台灣消費者，甚至行銷到全世界。\r\n 　「玉井之門」專門製造天然有機蜜餞，使用更天然更衛生的製作方式，讓蜜餞不但保有原本的美味，也變得很健康，其中以芒果乾為的主打產品，其他熱賣產品包括黑糖芒果乾、水蜜桃乾、鳳梨乾、芭樂乾、情人果乾、蕃茄乾、楊桃乾、葡萄乾、無花果乾、茶葉梅、甜乾梅等數種，送禮自用兩相宜。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151028152343.jpg', '臺南市', '南化區', 23.0517371, 120.49912),
(96, '原燒黑糖香', '台南市南化區(村)128號', '以自然農法種植甘蔗，栽培過程不使用任何農藥，並採用人工煉製，100％甘蔗原汁熬煮製成，不含防腐劑、人工香料，是最自然的產品，而且傳統手工黑糖精緻程度低，保留了礦物質、維生素，糖蜜滋味更帶著一般砂糖所沒有的炭燒香氣。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151028151330.jpg', '臺南市', '南化區', 23.043118, 120.476416),
(97, '酒粕櫻花蝦XO醬', '宜蘭縣頭城鎮更新路126-50號', '採用來自宜蘭大溪漁港稀有珍貴的櫻花蝦為料理主軸，加入干貝、鹹魚等數種食材，再搭配酒莊製酒產出的米酒酒粕提味，讓清香的米酒酒粕帶出櫻花蝦鮮甜的滋，打造台灣獨一無二的手工櫻花蝦XO醬。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151023181233.jpg', '宜蘭縣', '頭城鎮', 24.9036307, 121.862429),
(98, '大膽不敵-牛蒡蔘', '台南市將軍區忠興里189號', '牛蒡根部深植土內，蘊含大地精華，是古傳千年的天然食品，本產品堅持使用在地新鮮牛蒡，以新穎技術粹煉製成，嚼勁十足，令人一吃就上癮，是您最佳零嘴的休閒小品。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151028152551.jpg', '臺南市', '將軍區', 23.2018481, 120.16326),
(99, '好了梅！梅果小舖', '台南市楠西區香蕉山53-1號', '與大地同呼吸的台南梅嶺農夫，在製作時保留梅子原始風味，帶著紫蘇香氣的梅沾醬陪您迎接每朝；梅果醬佐午茶的豐富風味；疲累時再沖泡一杯梅精蜂蜜慰勞身心。這是採摘青梅時想要給您的用心，並且讓它以最好的方式呈現。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151028153838.jpg', '臺南市', '楠西區', 23.1859399, 120.53455),
(100, '柚之寶淨白系列', '台南市麻豆區興國路 58 號', '文旦，眾所週知皆會想到麻豆，麻豆有三寶：文旦、白柚、酪梨，其中柚子就包含了二寶，利用柚香精油所作成的衛浴系列產品。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151028153959.jpg', '臺南市', '麻豆區', 23.176068, 120.246536),
(101, '文旦柚茶', '台南市麻豆區興國路 58 號', '文旦的故鄉－麻豆區，春天一到，純潔雪白的柚花遍開各地，花香撲鼻、浪漫景色，搭配竹炭麻豆特有的文學氣息，以及鎮上巴洛克式建築，令人流連忘返。麻豆區農會堅持以天然優質為研發原則，提供您賞聞柚花清香，品嚐柚肉香甜，全方位體驗優質文旦的好處。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151028154211.jpg', '臺南市', '麻豆區', 23.176068, 120.246536),
(102, '旺來伯-稻香鳳梨酥', '台南市善化區中山路 242 號', '代表善化在地的味道，堅持真材實料與古法傳承，鮮採鳳梨熬製入餡，外搭本地養生米麩酥皮。嚴選原料素材，真誠用心採少糖少油製作，融入健康養生概念，口感豐實、濃郁香醇，值得品嘗分享。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151028154350.jpg', '臺南市', '善化區', 23.132757, 120.297647),
(103, '善緣女兒禮', '台南市善化區中山路 242 號', '以芝麻提煉而成的胡麻油，油質清澈、風味獨特，且品質優良有保障，並且富含對人體有益的不飽和脂肪酸，有助於保持健康及抗老。而芝麻麵口感Q彈，散發淡淡清香，以乾炒或湯食方式烹煮皆宜。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151028154445.jpg', '臺南市', '善化區', 23.132757, 120.297647),
(104, '新纖貨-鳳梨乾', '台南市新化區信義路450號', '精選一級品之『17號金鑽鳳梨』鮮果，整個製程僅有鮮果及砂糖，無添加任何防腐劑、色素及人工香料，讓您買的安心、吃的放心。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151028154605.jpg', '臺南市', '新化區', 23.0345014, 120.301965),
(105, '大目降胡麻系列-胡麻油大目降胡麻清油', '台南市新化區信義路450號', '遵循古法低溫釀造，滴滴香醇，顏色清澤，入喉甘香芳醇，適合低溫拌炒或涼拌料理，讓你吃得輕鬆健康無負擔。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151028154841.jpg', '臺南市', '新化區', 23.0345014, 120.301965),
(106, '毛豆麵', '台南市新市區復興路 1-1 號', '新市區舊名新港社，是早期希拉雅平埔族主要聚落，早期毛豆栽培鼎盛，農會本著發展農特產伴手理念，創新飲食風味，以毛豆為素材，不添加防腐劑，揉合毛豆佈在麵條上，綠色蔬菜健康的新主張，滑溜爽口，誘人口感，新市毛豆麵是愛麵族的新歡。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151028154946.jpg', '臺南市', '新市區', 23.0792847, 120.295499),
(107, '純南瓜粉', '宜蘭縣壯圍鄉新南路107-7號', '宜蘭旺山休閒農場，主要栽種各式各樣品種南瓜，瓜果隧道中充滿驚奇，總是吸引著許多遊客駐足觀賞，南瓜粉純淨天然，沖泡飲用或是煮濃湯使用，健康又美味。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151023181345.jpg', '宜蘭縣', '壯圍鄉', 24.7277051, 121.808861),
(108, '冷凍毛豆莢', '台南市新市區復興路 1-1 號', '選用優良品種，契作農田生產管理皆有履歷記錄；為確保鮮甜原味，嚴選於清晨採收，4小時內加工完成。', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151028155038.jpg', '臺南市', '新市區', 23.0792847, 120.295499),
(109, '逗呷禮盒-新市好濃味', '台南市新市區復興路 1-1 號', '毛豆被稱為「貧民之肉」，富含蛋白質、礦物質、維生素與膳食纖維，營養成份容易被人體吸收。新市毛豆粒粒飽滿肥美，品質優良，所製成的麵及香鬆，散發清淡豆香。飯後來杯蓮霧茶，氣味芬芳，身心都舒暢。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151028155148.jpg', '臺南市', '新市區', 23.0792847, 120.295499),
(110, '竹好炭伴手禮', '台南市龍崎區崎頂里7鄰223號', '為推廣本鄉竹林產業產值低及無利用價值竹材再利用，提高竹子產業及其附加價值，並活絡地方文化及休閒產業。「竹好炭」意味著「祝好賺」，藉由開發多樣化竹炭相關系列產品，以增加在地青年就業機會、農民收入、農會收益，創造本鄉竹林產業再利用和無限生機。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151028170734.jpg', '臺南市', '龍崎區', 22.9688808, 120.383901),
(111, '龍崎湛炭', '台南市龍崎區崎頂里7鄰223號', '竹炭譽為「炭中的黑鑽石」普遍運用一般生活中有淨化水質、除臭、除溼等功能，且竹子生長，能達到水土保持，環保效益很高，是我們追求生活美學中，最佳的綠色環保日用品。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151028170810.jpg', '臺南市', '龍崎區', 22.9688808, 120.383901),
(112, '真空綠竹筍', '台南市關廟區中正路963號', '真空筍為高纖維健康食品，主要特色是利用高溫高壓蒸煮技術，將竹筍鮮、嫩、甜鎖住，加工過程不中沾水，不添加防腐劑，真空殺菌，安全衛生。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151028170950.jpg', '臺南市', '關廟區', 22.9624947, 120.32842),
(113, '關廟鳳梨麵', '台南市關廟區中正路963號', '採用在地新鮮台3號鳳梨，遵循嚴謹製麵流程，堅持不填加任何防腐劑，人工打理製作，微黃色的麵體散發鳳梨清香，口味獨特，耐煮不爛。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151028171132.jpg', '臺南市', '關廟區', 22.9624947, 120.32842),
(114, '金棗健康家族', '宜蘭縣礁溪鄉興農路127號', '金柑又稱金橘或金棗,果實呈金黃色,果肉酸中帶甜.礁溪鄉地層結構特殊,全年氣候溫和,雨量充沛,是著名著溫泉風景區.適合金棗的種植,所生產的金棗質佳味美,有金棗的故鄉美譽.礁溪鄉農會為發展多元產品,委由專業認證的杏輝藥廠研究開發「金棗軟糖」保留金棗的健康元素與美味分享給大家。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151023181428.jpg', '宜蘭縣', '礁溪鄉', 24.8190818, 121.775228),
(115, '農情鳳禮鳳梨酥+鳳梨乾禮盒', '台南市關廟區中正路963號', '臺灣首創小麥胚芽製成的餅皮，少油少糖天然養生概況，嚴選關廟鳳梨遵古法熬煮，100鳳梨內餡真材實料，邀您細細品嚐。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151028171453.jpg', '臺南市', '關廟區', 22.9624947, 120.32842),
(116, '鹽水番茄米餅系列鹽水區農會番茄米餅', '台南市鹽水區西門路21號', '嚴選鹽水特產蕃茄，熬煮番茄鮮果的原汁，淋上飽滿的香脆米粒，充分拌勻，茄汁果香自然包覆，美味養生自然無負擔。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151028171646.jpg', '臺南市', '鹽水區', 23.319378, 120.265784),
(117, '手作果干', '台南市新化區大坑里82號', '大坑休閒農場的阿公阿嬤自己生產水果，每季盛產時，就會將水果烘烤乾以保存作為點心，歡迎客人到大坑作客，希望能將阿公阿嬤的果干，用最善意的方式讓大家延續旅遊的記憶。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151028171805.jpg', '臺南市', '新化區', 23.0068316, 120.390406),
(118, '玉荷甘果', '高雄市大樹區大坑里大坑路108-18號', '自麵包師傅吳寶春以「荔枝玫瑰麵包」勇奪世界麵包大賽冠軍後，傳統美食荔枝乾因此備受外界矚目。「玉荷甘果」嚴格選別頂級珍貴玉荷苞，採用最低破壞加工方式所製成，肉質厚實Q軟、籽粒細小、口感香Q，特有香氣(類似玫瑰香氣)及籽粒小易於剝食果肉，有別於市面上的黑葉荔枝乾或其他甘果類之產品。「玉荷甘果」適合老少咸宜居家休閒食品，甜湯粥品之引味及烘焙蛋糕。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151028171940.jpg', '高雄市', '大樹區', 22.7126936, 120.440828),
(119, '幸福廚房', '高雄市甲仙區新興路9號', '有機梅精醬油及有機黃梅和風醬，使用在地有機青梅與有機醬油工廠合作研發生產，這是目前市場獨一無二的有機梅精醬油，期能帶給消費者一個食的安全，打造安心的幸福廚房。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151028172315.jpg', '高雄市', '甲仙區', 23.0735195, 120.580547),
(120, '福花茶王', '高雄市田寮區新興村崗南路 4-1號', '自然、養生、保健的福花，是花，不是茶葉，因產地陽光足、土質佳、山坡地、水質優，四合一優越條件下，所結成的龍眼花及龍眼蜜從清朝、日據時代到至今，成為大岡山的特產，再加上生產者愛心照顧，十二年來，口碑遠播。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151028172422.jpg', '高雄市', '田寮區', 22.8633474, 120.401191),
(121, '真愛吉緣訂婚禮盒', '宜蘭縣礁溪鄉興農路127號', '蘭陽平原是台灣重要的米倉之一， 礁溪以溫泉而聞名，素有小北投之稱，除了泡湯，礁溪人引用溫泉水種植的溫泉蔬菜，鮮脆美味口碑佳，著名全台，近年精心培育的溫泉香米質優味美稀有且珍貴，為礁溪溫泉農業開闢新的里程碑。\r\n金棗為礁溪主要特產，充滿健康陽光的形象，累累的橙果誘人味蕾，林美、匏常二結三村栽種近達一百餘公頃，並訢有金棗匝工處理中心、金棗文化餡，研發加工、推廣金棗產品，風味多樣化有原味梅香金棗、金棗果醬、蜜汁金棗、金棗酥、牛舌餅、金棗餅、金棗軟糖、金棗檸檬茶，樣樣香氣濃郁令人喜愛。\r\n礁溪鄉農會感念大地的恩賜，為協助地方農業發展與農產品的推廣 , 發揮創意把溫泉香米的濃郁鄉情，刻劃礁溪人對稻米的珍愛情懷，將「溫泉香米」與「金棗喜餅」精心規劃一份屬於礁溪道地的禮盒組裝，結合「棗生貴子」的概念，創造出「棗、稻幸福」的新點子，推出「真愛‧吉緣」的女兒禮喜餅，並以「真愛在礁溪，相遇總是吉緣，戀戀大地情，永浴在溫泉故鄉」祝福新婚兒女找到幸福。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151023181532.jpg', '宜蘭縣', '礁溪鄉', 24.8190818, 121.775228),
(122, '台園膠原蛋白美人凍禮盒', '高雄市林園區漁港路2號', '本會通過HACCP及ISO9001 22000系統國際認證，具有符合衛生安全及專業具國際水準的加工設備及專責人員製作產品。膠原蛋白凍係經由魚體萃取而來，純天然的膠原蛋白經18.5個小時的製造過程，嚴格控管品質。本產品通過SGS台美檢驗和新竹食品研究所檢驗，高含量膠原蛋白未含任何化學成分及防腐劑，對身體無負擔，任何年齡皆可以食用。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151028172628.jpg', '高雄市', '林園區', 22.4826554, 120.398438),
(123, '幸福蕉點', '高雄市旗山區復新街32號', '遵循古法精製，採純手工加工製作，百分之百採用本地旗山香蕉，無添加任何人工色素、防腐劑，是天然健康的休閒零嘴的好選擇。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151028172853.jpg', '高雄市', '旗山區', 22.8850276, 120.482137),
(124, '花嫁-濃情陶米禮盒', '高雄市美濃區泰安里中正路一段1號', '結合美濃當地農產好米與當地陶藝產業，挑選一等良質米「高雄145」，其飯粒香Q、飯香濃郁。且沿襲客家習俗搭配陶藝碗筷，象徵子女傳承衣缽，成家立業。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151028173111.jpg', '高雄市', '美濃區', 22.902171, 120.541406),
(125, '大樹張媽媽桑椹系列產品', '高雄市大樹區興田里(路)116-1號', '大樹張媽媽的桑椹採自然農法，無毒栽培，保障消費者的安全，保證不加其他人工香料，自用送禮兩相宜，是您來大樹張媽媽最佳的好伴手。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151028173301.jpg', '高雄市', '大樹區', 22.7289906, 120.405633),
(126, '戀戀蚵仔寮-幸福伴手禮盒 (野生烏魚子禮盒)', '高雄市梓官區漁港二路32號', '梓官擁有得天獨厚的海岸線，每年冬至前後十天，烏魚大批出現於台灣海峽附近洄游產卵，數百年來從未失信過。透過梓官頂級烏魚子分享給心中最重要的人，傳遞幸福約定。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151028180654.jpg', '高雄市', '梓官區', 22.7258567, 120.256825),
(127, '戀戀蚵仔寮-極饌御宴禮盒', '高雄市梓官區漁港二路32號', '烏魚在漁民心中為一種尊貴的經濟活動代表，其本身即帶有著希望、豐收、財富、圓滿的意涵。精選烏魚三寶：烏', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/292 戀戀蚵仔寮-極饌御宴禮盒_20111020 (原始檔).jpg', '高雄市', '梓官區', 22.7258567, 120.256825),
(128, '蒲燒鰻', '高雄市湖內區中正路二段158號', '選用上乘等級鰻魚搭配獨特醬汁，肉質細膩軟嫩，味道鮮美，入口即化，製作過程講求真材實料，不添加化學物品調味，含有豐富的蛋白質、維生素A、E與多量的鈣，營養價值豐富。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151028180757.jpg', '高雄市', '湖內區', 22.9081774, 120.208939),
(129, '溫泉米粉', '宜蘭縣礁溪鄉興農路127號', '口感香Q、綿密好吃，以冰水研磨法一貫作業完成，食用安全、衛生。', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151026094940.jpg', '宜蘭縣', '礁溪鄉', 24.8190818, 121.775228),
(130, '虱想起禮盒', '高雄市彌陀區南寮里漁港一街60號', '首創「魚」口味喜餅！與虱目魚脯、魚鬆組合成訂婚禮盒，取代高糖分的喜餅，營養健康。', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151028180845.jpg', '高雄市', '彌陀區', 22.7637416, 120.235443),
(131, '「思慕‧魚」系列-雙寶組合禮盒', '高雄市彌陀區南寮里漁港一街60號', '國境之南的純，呈現最為樸實的佳餚，撼動最為挑剔的味蕾。「虱目魚雙寶禮盒」再一次將最原始的口味變成最回味的滋味，將最直接的口感創造出更耀人的質感。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151028180919.jpg', '高雄市', '彌陀區', 22.7637416, 120.235443),
(132, '鳳梨發酵醋', '高雄市大樹區興田里興田路116-1號', '取自高雄大樹在地鳳梨，採純天然釀造，不添加任何化學原料。讓消費者喝到最安心又健康的醋。', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151029112037.jpg', '高雄市', '大樹區', 22.7466957, 120.447989),
(133, '芒果冰淇淋', '屏東縣枋山鄉枋山村國中路1號', '使用高優質枋山芒果，加上濃醇的奶香，入口即化的綿密感，一種協調味道在口中讓味蕾陶醉，享受冰冰甜甜的美麗境界！\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151029112256.jpg', '屏東縣', '枋山鄉', 22.2639534, 120.660721),
(134, '無添加芒果乾', '屏東縣枋山鄉枋山村國中路3號', '手作芒果乾，由成熟芒果新鮮製作，保留住自然果香甜，不加糖不含防腐劑，是超人氣健康新美食!\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151029112334.jpg', '屏東縣', '枋山鄉', 22.263642, 120.66072),
(135, '果然好味', '屏東縣枋山鄉善餘村德隆路25-1號', '嚴選來自屏東枋山新鮮甜美的愛文芒果，製程中不添加人工色素、香料及防腐劑，經 24 小時烘烤，保留下最飽滿的芒果滋味，咬一口，特有的香氣撲鼻而來，特殊的口感只有吃過才能體會，讓人戀戀好滋味、天天好心情。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151029112410.jpg', '屏東縣', '枋山鄉', 22.1910849, 120.690609),
(136, '旗魚鬆', '屏東縣東港鎮新生三路175號', '東港旗魚白中透肉色、味道甘美，是養分極高的魚類，在台灣及日本常是壽司與生魚片中的珍品。東港區漁會用心製作嚴選新鮮旗魚入口即能感受香酥鮮美的原始海洋風味。本產品係選用新鮮旗魚肉特製而成，不添加任何防腐劑及色素，是佐飯的上上之選。您充分體驗海味故鄉「東港」的懷古鮮味。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151029112451.jpg', '屏東縣', '東港鎮', 22.4675985, 120.442814),
(137, '湯戀組合禮盒', '宜蘭縣礁溪鄉興農路127號', '著名的溫泉鄉，好山好水的地理環境，以溫泉的圖像結合「湯」的概念，營造礁溪鄉溫泉蔬果的特色，充滿活力與生機，代表礁溪鄉農特產品獨創的品牌標誌。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151026095110.jpg', '宜蘭縣', '礁溪鄉', 24.8190818, 121.775228),
(138, '枋寮鈣多情-芒果鱙仔魚', '屏東縣枋寮鄉保生村(路)437號', '枋寮漁港盛產鮮美優質的魩仔魚、鱙仔魚、享有『魩之鄉』美譽，鈣讚魚片採用新鮮海魚的休閒食品，讓人停不了口，是最佳品茗良伴、老少皆宜。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151029112649.jpg', '屏東縣', '枋寮鄉', 22.3447209, 120.607044),
(139, '稻鴨香伴手禮', '屏東縣高樹鄉泰山村產業路329號', '以源自大武山水源保護區之山泉水孕育，採用老祖先最具智慧的耕作方式─稻鴨共作以達到病蟲害防治，米粒大、外觀晶瑩剔透、口感好、風味佳。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151029112750.jpg', '屏東縣', '高樹鄉', 22.7951794, 120.610959),
(140, '鶉園鵪鶉元氣蛋捲', '屏東縣萬巒鄉萬金村營區路1巷10號', '農場主要飼養無藥物殘留之法國鵪鶉，運用每日嚴選現產鵪鶉蛋，搭配頂級食材，每日限量製作的元氣蛋捲，蛋香濃厚，新鮮可口，能讓每天充滿活力!\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151029112827.jpg', '屏東縣', '萬巒鄉', 22.612133, 120.625259),
(141, '溫泉糙米茶', '宜蘭縣礁溪鄉興農路127號', '溫泉糙米茶採用在地的溫泉糙米與天然漢方-決明子加以組合，到礁溪泡湯旅遊之後，再喝上一杯「溫泉糙米茶」充滿幸福快樂的感覺！\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151026095201.jpg', '宜蘭縣', '礁溪鄉', 24.8190818, 121.775228),
(142, '1. 洛神養生果凍、2. 水果酒禮盒、3. 原汁禮盒', '台東縣太麻里鄉泰和村曙光街2號', '有洛神、桂圓、芒果等3種口味，產品未添加防腐劑，期限3個月。', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151029113004.jpg', '臺東縣', '太麻里鄉', 22.6018231, 120.998081),
(143, '釋迦、洛神休閒食品', '台東縣太麻里鄉泰和村曙光街2號', '釋迦、金針、洛神、桑葚是太麻里地區主要特產，特製成可口的小點心，每一種口味皆散發其香醇風味，經典好滋味。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151029113111.jpg', '臺東縣', '太麻里鄉', 22.6018231, 120.998081),
(144, '洛神元氣餅', '台東縣太麻里鄉泰和村曙光街2號', '為薄餅狀，吃起來除了有奶香還有洛神的酸甜口感，好吃清爽不油膩。', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151029113147.jpg', '臺東縣', '太麻里鄉', 22.6018231, 120.998081),
(145, '養生香椿茶', '台東縣台東市更生北路118號', '香椿，相傳古老的部落長老總在瘴癘叢生的夏季之前，採擷香椿在每年春天萌芽時其新梢之嫩葉芽，此時的香椿鮮嫩味美，通常一部分食用，其餘則乾製收藏備用。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151029113357.jpg', '臺東縣', '台東市', 22.778972, 121.124906),
(146, '台東有機竹香咖啡禮盒', '台東縣台東市更生北路118號', '1904年日本人引進阿拉比卡種Arabica coffee 咖啡由台東往北發展…，極特殊軟性咖啡豆是白波旁咖啡的特色。遵守自然生成定律，以自然農法栽培出清香甘醇的能量咖啡。\r\n台東縣卑南鄉龍過脈卑南族古地名（NADANAW）盛產竹林。地處中央山脈與海岸山脈（俗稱九港風）的太平洋黑潮風流域，每年冬季東北季風來臨時，竹葉飄香就是咖啡成熟時。咖啡農對土地與大自然的感恩，分享竹香與咖啡收成的喜悅。\r\n成分：阿拉比卡種咖啡豆\r\n保存期限：12個月\r\n保存方式：請置於乾燥處，避免陽光直射，拆封後請即沖泡飲用。\r\n生產單位：台東\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151029113435.jpg', '臺東縣', '台東市', 22.778972, 121.124906),
(147, '果子狸咖啡', '台東縣台東市更生北路118號', '享受一杯幫助地球更健康的好咖啡，來自古老傳說中的麝香咖啡，體外熱發酵科技生產。太平洋黑潮風蘊育，自然農法栽培，台東在地太平洋海岸山脈都蘭山麥飯石，竹炭乾燥烘培。果子狸咖啡為發酵後提煉的益菌和咖啡豆混合，口感順口好入喉，值得大家品嘗。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151029113817.jpg', '臺東縣', '台東市', 22.778972, 121.124906),
(148, '鹿鳴枇杷梅', '台東縣台東市更生北路118號', '《神農本草經》內載有枇杷及梅子食用之功效，將枇杷葉裡萃取物與醃漬的梅子做個結合，除使梅子有酸甜口味，且帶淡淡的枇杷香氣外，更能同時吸收兩者的營養成分。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151029113851.jpg', '臺東縣', '台東市', 22.778972, 121.124906),
(149, '山海紅鑽', '台東縣卑南鄉溫泉村溫泉路 388 號', '有植物界「紅寶石」之稱的洛神花，來自她豐美的故鄉──台東，在地製成的紅蜜汁、酸甜味，手捧著幸福的弧線，品嚐一口洛神花蜜餞，笑開了眼、漾紅的甜。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151029114002.jpg', '臺東縣', '卑南鄉', 22.6955793, 121.022134),
(150, '臺灣臺東好茶', '台東縣卑南鄉溫泉村溫泉路 388 號', '紅烏龍係結合烏龍茶與紅茶之特色所創造的一種新興特色 茶，屬重發酵、著重烘焙的半球形烏龍茶，茶湯水色琥珀橙 紅如紅茶，滋味則如烏龍茶，具熟果香，醇厚圓滑、耐泡， 不但適合一般熱水沖泡，冷泡更顯其特色，廣獲市場好評。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151029114111.jpg', '臺東縣', '卑南鄉', 22.6955793, 121.022134),
(151, '洛神花軟Q糖', '台東縣卑南鄉溫泉村溫泉路 388 號', '「洛神軟Q 糖」採台東火紅盛開的洛神花萼製作，保留了其原始的酸味及香氣，以優良的技術開發洛神花新口味，符合小朋友、長青者及各種年齡層的消費者的口感，是適合全家人的休閒零嘴。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151029114153.jpg', '臺東縣', '卑南鄉', 22.6955793, 121.022134),
(152, '布農小米酒 davuz', '台東縣延平鄉桃源村11鄰191號', '台東延平鄉布農部落以在地天然原料釀製阿嬤的小米酒，是為了祭天、感恩。布農小米酒 (davuz) 加上著友情的編織，希望分享給您這份甜美的喜悅。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151029114228.jpg', '臺東縣', '延平鄉', 22.9158013, 121.083538),
(153, '台11線牛肉乾', '台東縣成功鎮新生路55號', '台11線牛肉乾採用國產牛肉，肉質細緻香甜，品質天然，且低膽固醇、無腥味，輕鬆享受無負擔。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151029114931.jpg', '臺東縣', '成功鎮', 23.1031521, 121.378469),
(154, '池農養生米麩', '台東縣池上鄉中山路302號', '由新武呂溪的礦物質及秀姑鸞溪的山泉所共同孕育出的池上米，米質冠全台，依循古法，將池上地區所生產「台稉二號」糙米，加入黑糯米、薏仁、小米及蕎麥等國產穀物與種子，利用爆米花的方式膨發，並加以研磨成細粉後，製成池農養生五穀粉，最適合成長發育中的小孩或是年老的長輩補充營養之用。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151029114457.jpg', '臺東縣', '池上鄉', 23.1250621, 121.21929),
(155, '南澳椴木香菇伴手禮盒', '宜蘭縣蘇澳鎮龍德里福德路156號', '百分百椴木香菇產自好山好水南澳地區，當地具有多個部落，且每個部落皆有其落部特色，是來到宜蘭南澳鄉旅遊最佳的伴手選擇。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151026113523.jpg', '宜蘭縣', '蘇澳鎮', 24.6404134, 121.831706),
(156, '米鄉-紫米餅', '台東縣池上鄉中山路302號', '紫米在古時是皇帝食用的米，故稱「貢米」，阿美族人將之視之為老人養生米，也因此在花東地區栽培黑糯米以阿美族為主，本產品將池上輔導契作生產之稻米，經過碾製而成碎米粒，再給予膨發之後，做成紫米餅，香脆好吃，也提供米餅市場新選擇。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151029141359.jpg', '臺東縣', '池上鄉', 23.1250621, 121.21929),
(157, '池農米蛋捲', '台東縣池上鄉中山路302號', '池上生產的稻米經研磨機成米穀粉，加入放山雞蛋，經烘培後的口感特殊，是健康米食新選擇。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151029141435.jpg', '臺東縣', '池上鄉', 23.1250621, 121.21929),
(158, '池上米餅禮盒', '台東縣池上鄉中山路302號', '嚴選池上優質好米，細心拌入伯朗大道的稻香，化為美味與情感兼具的池上米餅，是您最佳的伴手禮選擇。', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151029141509.jpg', '臺東縣', '池上鄉', 23.1250621, 121.21929),
(159, '米戀茶緣禮盒', '台東縣鹿野鄉鹿野村中華路二段 25 號', '以天然又實用的縱谷福鹿茶、米，取代傳統喜餅禮盒，符合現代人健康、安全的需求。', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151029141620.jpg', '臺東縣', '鹿野鄉', 22.9116099, 121.135994),
(160, '玉溪好米', '花蓮縣玉里鎮大禺里166-13號', '外盒金色象徵微金黃色到歲，結合玉溪虹橋，讓人直接連想好米來自好山，好水的玉里鎮，產品小巧精緻，行銷通路可用於送禮、活動贈品及旅遊伴手禮。現代人吃的來源方面注重養生及產品品質，內庄獲得日本金賞獎玉溪米(台梗16號)1Kg裝，口感香Q，不僅價格經濟實惠，送禮大方，讓人感受到『金賞好米』不同凡響之處。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151029141712.jpg', '花蓮縣', '玉里鎮', 23.3804975, 121.32868),
(161, '當歸心圓夢情禮盒', '花蓮縣玉里鎮大同路24號', '「當歸心圓夢情」是描述東方人兒女之情的故事，仿古人以藥寄情的方式，寄以當歸(歸來之意)，期待胸懷遠志的男方，能夠早日衣錦還鄉，成家立業，圓夢男女之情，唯之「當歸之喜」是最佳傳遞祝福的伴手禮。\r\n　當歸是女性生理調節聖品，含豐富的維生素A、B、鐵、鎂及鈣，是不可或缺的營養補充。台灣當歸的故鄉花蓮，以專業化的農耕方式，結合自然養生保健風潮，用汗去種植耕作，用心去栽培管理，用愛去施肥灌溉，將農民辛苦栽培的農作物，製作最頂級的產品，用衛生、安全、建康的概念和嚴格把關，經由細心呵護頂級品質，製成為傳遞祝福的伴手禮。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151029142112.jpg', '花蓮縣', '玉里鎮', 23.325412, 121.31628),
(162, '富麗養生粥', '花蓮縣富里鄉羅山村9鄰東湖6號', '採用富里鄉內農友所生產的無毒農產品:當歸、山藥零餘子、芋仔地瓜，及富里鄉農會所生產的富麗米，經過高壓高溫殺菌後，所料理出來的美味養生粥，希望能讓消費者品安心嚐到更獨特、美味的粥品。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151029142335.jpg', '花蓮縣', '富里鄉', 23.1838646, 121.296967),
(163, '牛奶系列天然沐浴用品', '花蓮縣鳳林鎮永福街20號', '位於台九線231.5公里的兆豐農場，浪漫迷人的景致搭配用心塑造的歐式風情，令人彷彿打開奇幻的任意之門，走入美麗動人的幻境。這裡擁有天然花海、萬里晴空之美景，微風、鳥鳴與養顏美容的碳酸溫泉SPA，就是要將農場打造成渡假天堂。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151029142515.jpg', '花蓮縣', '鳳林鎮', 23.8037226, 121.456414),
(164, '瑞穗柚花茶', '花蓮縣瑞穗鄉中山路一段128號', '每年三月間是文旦柚樹開花的時節，當柚花遇到瑞穗有名的天鶴茶，就成了香氣迷人的上品柚花茶。運用了一年才綻開一次的柚花與茶葉共同烘焙而成的花茶，不但可 以喝到茶葉的甘醇，回甘時還帶有誘人的天然芳香。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151029142603.jpg', '花蓮縣', '瑞穗鄉', 23.496431, 121.381484),
(165, '茶籽油禮盒', '台北市南港區南港路一段173號', '南港因當地海拔約200至300公尺，土壤富含岩礫，適合茶樹的生長，本茶籽油精選高品質茶籽，經過日曬、脫殼、炒熟、壓榨、過濾、沉澱、等複雜步驟煉製而成，是茶籽油首選。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151026134158.jpg', '臺北市', '南港區', 25.0551711, 121.61299),
(166, '伊糯千金', '花蓮縣壽豐鄉豐文路105號', '黑糯米性溫，在本草綱目中記載：『溫養五臟益脾胃、補中益氣、縮小便』。適量食用，益養肝血、補腎 氣、預防骨質疏鬆、延緩人體老化，所以也有『貢米』的稱號，阿美族人將之視為老人的養生米，且任何人皆可食用，更是婦女朋友的養生美容聖品。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151029142751.jpg', '花蓮縣', '壽豐鄉', 23.8423111, 121.502628),
(167, '花蓮鰹干貝XO醬', '花蓮縣花蓮市港濱路37號', '嚴選當季最肥美鰹魚，佐以金華火腿及干貝當配料精製，口味微辣，拌飯拌麵兩相宜。', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151029143002.jpg', '花蓮縣', '花蓮市', 24.0062297, 121.638531),
(168, '健康要裝蒜-健康調味禮盒', '金門縣金城鎮民生路6號', '需要加鹽來抑制腐敗菌，採用黑蒜頭取代黃豆原料所釀造出來的醬油，因為黑蒜頭本身具有抗菌功效，可降低鹽的用量，加上其獨特香味，清爽口感，瞬間使美味鮮活起來！而一公斤的黑蒜僅能精華萃取出250c.c.的黑蒜醬，香醇濃郁，立即感受。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151029150448.jpg', '金門縣', '金城鎮', 24.4329217, 118.320407),
(169, '醋老爺健康日記', '宜蘭縣員山鄉茄苳路3號', '用本土100天然純蜂蜜作基底與純淨雪山泉水釀造，加上首創的「雙層三階多菌相工法」完全發酵。每天一小瓶果醋來寫自然養生日記，你今天「醋」進健康了嗎？\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151029150532.jpg', '宜蘭縣', '員山鄉', 24.7460167, 121.708869),
(170, '戀戀大湖', '苗栗縣大湖鄉富興村八寮灣34-7號', '大湖酒莊是亞洲第一個草莓酒製酒產地，有嚴謹製造過程及結合在地豐富人文農村，戀戀大湖有亮麗的橘黃色澤，香甜的草莓果香躍然而出，在唇齒間漸漸融化的甜蜜滋味，令人回味，冰鎮享用後風味更佳。\r\n【農業易遊網提醒您：飲酒過量有礙身體健康，喝酒不開車】\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151029150702.jpg', '苗栗縣', '大湖鄉', 24.4402288, 120.876103),
(171, '柚香和風沾醬', '新北市八里區中山路二段 366 號', '山藥的盛產季節是在每年的十月至十二月間，營養豐富口感佳，最適合各年齡層食用。', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151026113736.jpg', '新北市', '八里區', 24.778289, 120.988108),
(172, '紅棗干', '苗栗縣公館鄉館東村大同路266號', '公館得天獨厚，具有特殊氣候與土質，為全台唯一生產紅棗地區，是紅棗的家！每年七、八月間觀光果園開放遊客採果，紅棗富含的營養成份，是提供藥膳之聖品。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151029150901.jpg', '苗栗縣', '公館鄉', 24.5046407, 120.827364),
(173, '炭晶蛋捲', '苗栗縣造橋鄉造橋村錦成東路8-5號', '成份：麵粉、台灣優蛋、砂糖、奶油、竹碳粉、白芝麻、鹽\r\n保存方式:常溫（本產品純手工精製，決不添加任何防腐劑）\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151029151000.jpg', '苗栗縣', '造橋鄉', 24.6356209, 120.875905),
(174, '柚鄉餅、副總統餅 (東閔餅)', '彰化縣二水鄉聖化村員集路三段678號', '依山（八卦山）傍水（濁水溪）的二水鄉，得天獨厚的好山好水，孕育出豐富的農特產品山坡地農產以白柚為最大宗產出的白柚，果肉多汁味甜，製作成的柚香餅（柚鄉餅）香酥可口。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151029151259.jpg', '彰化縣', '二水鄉', 23.810138, 120.616152),
(175, '紫香御品-香芋片', '台中市大甲區中山路一段372號', '採用大甲芋頭產銷班所生產的特級新鮮芋頭，透過製程的改良讓芋頭香氣能保存於芋片內，酥脆的口感及濃郁的芋頭香，讓您一口接一口。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151029161829.jpg', '臺中市', '大甲區', 24.334949, 120.615858),
(176, '楊媽媽菓子工坊-米麩金饌鳳梨酥', '台南市後壁區後壁里99號', '全國唯一由米麩製成的米麩金饡鳳梨酥，打開輕聞有淡淡的米麩香，細口品嚐，流露天然水果香，取自充滿陽光南台灣所栽種得頂級鳳梨，用純麥芽糖以古老方式熬煮成100％金黃色鳳梨餡，再搭配後壁米製成的營養米麩，高級麵粉、低糖、低油、低熱量製成的健康酥皮，不含香精，加入天然奶油、高級堅果所製成的米麩金饡鳳梨酥，是榮獲2008台灣燈會十大伴手禮糕餅類第一名的優質好伴手。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151029162155.jpg', '臺南市', '後壁區', 23.3632139, 120.357963),
(177, '三峽茶鄉、袋袋相傳禮盒', '新北市三峽區長泰街96號', '早期物資匱乏年代，茶農多以現成的肥料袋、麵粉袋拆開併接成大茶袋。日治時期農會草創的印記、三峽茶史印象與特種茶種，也象徵我們揉捻融合傳統與現代茶飲文化。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151026113824.jpg', '新北市', '三峽區', 24.933545, 121.376189),
(178, '棗紅公館蜜餞系列', '苗栗縣公館鄉館東村大同路266號', '棗紅公館蜜餞系列嚴選在地紅棗、紫蘇、洛神花、梅、金桔製造，不添加色素、防腐劑、漂白劑、甘味劑等，讓您食的安心。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151029163826.jpg', '苗栗縣', '公館鄉', 24.5046407, 120.827364),
(179, '鳥兒歡唱-養生禮盒組', '台東縣卑南鄉溫泉村溫泉路 388 號', '來自原鄉的紅藜、小米、樹豆，以自然的耕作方式栽培，營養豐富，邀您享用大地母親的恩賜。', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151029163903.jpg', '臺東縣', '卑南鄉', 22.6955793, 121.022134),
(180, '花蓮珍饌-肉乾與肉條系列', '花蓮縣玉里鎮城南五街27號', '取自最純淨無汙染的花蓮蓮貞豚豬隻後腿肉，搭配玉里米的米釀，以古早味手工限量製作米釀肉干與米釀肉條，限量嚴謹製作，是伴手禮最佳首選!\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151029164109.jpg', '花蓮縣', '玉里鎮', 23.3270702, 121.320723),
(181, '燒仙草禮盒', '新竹縣關西鎮北山里高橋坑10鄰6號', '以即溶燒仙草熱情好客的特性，搭配新時代健康養生「磁炭杯」， 具抗菌、抗臭、釋放微量負離子及將水活化等效能，能讓受禮者充分感受到所傳達的友誼、愛和溫暖，在寒冬中泡杯熱騰騰的燒仙草，會讓食客感到無比的滿足與喜悅。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151029164316.jpg', '新竹縣', '關西鎮', 24.8058508, 121.165311),
(182, '平溪原生種山藥', '新北市平溪區嶺腳里中華街15號', '「平溪山藥」經過嚴格篩選之優良品種來復育，每年的產季於9月中旬至12月期間，由於培植不易，所以更顯格外珍貴，口感Q、耐烹煮、風味極佳，可料理多道山藥佳餚。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151026113912.jpg', '新北市', '平溪區', 25.0254039, 121.739325),
(183, '臻品禮饌', '新北市石門區(村)中央路2號', '石門鐵觀音茶的香氣沈穩麥香、湯色金黃明亮；陳年老茶的香氣濃郁、滋味甘醇；鐵觀音原片茶的平易近人、入口生津，結合茶葉老、中、青三代特色，沉穩、中肯、實在推薦給您。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151026113950.jpg', '新北市', '石門區', 25.2908277, 121.567138),
(184, '陳年包種老茶', '新北市坪林區坪林村坪林街103號', '陳年包種老茶組合禮係與鶯歌區農會合作，以異業結合、互利行銷方式，將北台灣的特色茶-文山包種茶，與鶯歌本土陶藝大師創作的陶藝作品搭配組合，讓消費者於品嚐好茶之際，兼具收藏價值；禮盒設計以環保、鄉土、古樸、簡約為訴求。包種茶馳名中外，茶界人士譽稱「南烏龍，北包種」，尤以雲霧繚繞、蒼翠毓秀的坪林山區所產的包種茶品質最佳。而陳年包種乃結合了坪林的山川靈氣與大地精華，經過歲月的催化，長期的自然發酵與陳放，產生緩慢的轉化作用，散發著深沉而純熟自然的香氣；明亮橙紅色的茶湯，因長時間自然醇化，入喉餘韻盪漾，口齒留香。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151026114113.jpg', '新北市', '坪林區', 24.9350844, 121.710761),
(185, '包種美人雙饗泡', '新北市坪林區坪林村坪林街103號', '坪林包種茶香氣幽雅，外觀翠綠緊結且自然捲曲，沖泡後茶湯水色呈蜜綠金黃，入口細緻甘醇，蘊含淡淡的蘭、桂花香，經由口腔而透出鼻腔，使人滿口芬馥，神清氣爽，味覺與嗅覺同感濃郁，是茶中極品。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151026114207.jpg', '新北市', '坪林區', 24.9350844, 121.710761),
(186, '三好禮', '新北市坪林區坪林村坪林街103號', '包種茶油富含蛋白質、單元不飽和脂肪酸、維生素A、E等，對於高溫的穩定性和營養價值均優於一般市面上販售的沙拉油，不僅可養身保健，亦是美容良方。麵線以茶及山藥入味，採用多加水手打麵製麵方法，經低温高濕長時間熟成乾燥，麵線不會龜裂，品質安定，口感滑溜彈性足，湯麵、冷麵或乾拌皆宜。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151026114243.jpg', '新北市', '坪林區', 24.9350844, 121.710761),
(187, '金紅藷', '新北市金山區中山路267號', '甘藷酥餅：採用日本和果子的煉餡方式製作出香酥鬆軟的細緻外皮，內餡採用嚴選紅心甘藷融合傳統奶酥饀，以傳統的煉製手法製作出口感帶有甘藷的芳香及充滿Q軟的咬感。嚴選原料，作工札實；製造過程講求衛生健康的觀念，所以絕對不含任何人工添加劑。\r\n甘藷軟糖：以嚴選的金山紅心甘藷加上麥芽糖蜜製而成，口感微甜不膩，入口帶有甘藷的芳香，呈現有別於傳統風味的口感。製造過程講求衛生健康的觀念，所以絕對不含任何人工添加劑。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151026114320.jpg', '新北市', '金山區', 25.2225054, 121.636655),
(188, '金舞滂米', '桃園市蘆竹區龍安街二段968號', '阡陌交接的稻田，幻化成時尚的線條符號，黃色代表稻穗的成長，橙、紅色表現豐收的喜氣，不只滿足現代人的視覺期盼，也重新定位新概念的米食文化！\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151026114536.jpg', '桃園市', '蘆竹區', 25.0047037, 121.258777),
(189, '向日葵花茶', '桃園市觀音區大同里中山路二段833號', '利用向陽農場農場自產向日葵花採取其花瓣烘乾製成向日葵花茶包，並與健誠生物科技公司技術研發中心合作開發出富含在地特色且適合年輕族群喜愛口味的花茶，是絕佳飲品。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151026114659.jpg', '桃園市', '觀音區', 25.010732, 121.139732),
(190, '客家擂茶', '新竹縣北埔鄉北埔街94號', '擂茶(又名三生湯)是客家人招待貴客及飲用的茶點。擂為研磨之意，以陶製擂缽將茶葉，花生，芝麻等原料研磨後沖入沸水調勻後飲用。客家擂茶，茶味純，香氣濃，清涼解暑，不僅能生津止渴亦可當保健飲料，也是非常傳統的客家美食。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151026114805.jpg', '新竹縣', '北埔鄉', 24.700446, 121.055152),
(191, '手工蔬食蕃茄麵', '新竹縣芎林鄉文昌街120號', '整體包裝以樂活─LOHAS(Lifestyle of Health and Sustainability)作為主要發想，紙才選用貼近呈現自然風格的牛皮材質，版面設計在配色方面也盡量減少過於濃重的用墨，以大地色系(牛皮紙原色、鮮草綠)為主軸，來減少印刷油墨的汙染，以符合Susatinability概念精神，提升產品附加價值是一個重要的使命。產品命名也以樂活概念中的Slow Food(慢食運動─相對於時下盛行的Fast Food)作為出發點，強調輕食風格，減少過度添加、過度調味亦減少食用者負擔。', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151026114837.jpg', '新竹縣', '芎林鄉', 24.7742887, 121.080427),
(192, '瓊福滿溢', '新竹縣芎林鄉文昌街120號', '瓊，瓊玉。即為美好的意思，以客家人此辛勤栽種的優良好米，加上番茄所製作的番茄米粉，都是在地人的誠意獻禮，希望收到好米與米粉的人，都吃到這份用心與美味，並得到美好福分就像泉水倒到杯子裡滿出來一樣。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151026114912.jpg', '新竹縣', '芎林鄉', 24.7742887, 121.080427);
INSERT INTO `gift` (`id`, `name`, `addr`, `feature`, `picurl`, `city`, `town`, `lat`, `lng`) VALUES
(193, '柿餅加工系列產品-柿糬', '新竹縣新埔鎮楊新路一段322號', '來自新埔新鮮柿餅，製作而成的柿糬。本產品採用在地製作柿餅，結合客家麻糬，以花生及山藥為內餡,純手工製作，一入口有柿餅的自然香甜，QQ軟軟的麻糬,蘊藏著濃郁花生或山藥的口味，是一種古早味新感受。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151026115430.jpg', '新竹縣', '新埔鎮', 24.8312961, 121.088394),
(194, '客家柚子茶', '新竹縣新埔鎮楊新路一段322號', '傳承三代的柚子茶老師傅，靠著多年經驗製作，以整顆柚茶磚經過粉碎再分小包裝，熱水沖泡，一陣陣柚香、茶香、飄散出來，是傳承客家勤儉樸實味道，邀您細細品嚐。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151026115632.jpg', '新竹縣', '新埔鎮', 24.8312961, 121.088394),
(195, '台北珍情土肉桂伴手禮組', '台北市大安區復興南路一段390號14樓', '土肉桂茶調配了甜菊與魚腥草，以複方茶品的樣貌呈現。甜菊增添了入口的甜味，魚腥草則是健康取向，因為採用複方，揉合了肉桂的味道，不論聞起來或嚐起來都溫潤許多，在嘴裡停留時，甘甜的滋味最是明顯。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151023165917.jpg', '臺北市', '大安區', 25.0337719, 121.543272),
(196, '彩油四季-苦茶油', '苗栗縣三義鄉中正路80號', '本提煉茶油的茶果生長於無污染的大自然山林原野間，養分來自於天然無污染的肥沃大地，邀您品嚐最自然的滋味。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151026153407.jpg', '苗栗縣', '三義鄉', 24.4146177, 120.772363),
(197, '擂茶香柚酥禮盒', '苗栗縣三灣鄉中正路176號', '因得天獨厚地理條件種植出的文旦柚，皮薄多汁，果肉柔軟，質甜味美，更具有獨特的蜂蜜風味。將代表客家傳統美食的擂茶與其結合，絕妙的創新口味令人耳目一新。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151026153504.jpg', '苗栗縣', '三義鄉', 24.6535081, 120.956137),
(198, '草莓大福', '苗栗縣大湖鄉富興村八寮灣2-4號', '紅豆、草莓、糯米粉、玉米粉、水飴、砂糖。\r\n直接食用 \r\n常溫/冷藏：一天未食用完請冷藏保存。 \r\n大湖新鮮的草莓，酸甜的草莓，搭配著白Q的麻糬皮、濃郁的豆沙，讓人難忘的幸福滋味。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151026153547.jpg', '苗栗縣', '大湖鄉', 24.4402288, 120.876103),
(199, '草莓蜜果子', '苗栗縣大湖鄉富興村八寮灣2-4號', '將當季甜度與大小一致新鮮草莓，利用整顆果實製成，完全不含人工色素、香料及糖精。100％大湖嚴選季節限定，富有酸酸甜甜的滋味，獨特口感令人回味。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151026153650.jpg', '苗栗縣', '大湖鄉', 24.4402288, 120.876103),
(200, '紅運高棗', '苗栗縣公館鄉館東村大同路266號', '公館鄉紅棗品種可以追溯至120年前，地方耆老陳煥南從廣東潮安引進栽種至今，紅運高棗精力湯是精選紅棗搭配五穀粉、豆粉混勻組合而成，無添加人工香料、色素及防腐劑，營養豐富均衡美味。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151026153804.jpg', '苗栗縣', '公館鄉', 24.5046407, 120.827364),
(201, '幸福禮盒', '苗栗縣公館鄉館東村大同路266號', '紅棗、紫蘇均為公館特有農產，長久以來均被視為中藥材使用。公館鄉農會食品廠特別將紅棗製成可以一嘴一口的健康零嘴的紅棗干；紫蘇則開發成可作為佐菜香料的紫蘇醬，夾土司、配稀飯、拌飯或拌麵皆適宜。紅棗干和紫蘇醬再搭配客家名產福菜而組成禮盒，口味多元，送禮大方、又健康\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151026153838.jpg', '苗栗縣', '公館鄉', 24.5046407, 120.827364),
(202, '好采福', '苗栗縣公館鄉館東村大同路266號', '秉持傳統製法並加以技術改良，將成熟的芥菜用心醃製，並經繁複多次的曝曬、翻面、發酵，製成風味濃郁、醇香且甘美的福菜與榨菜。且以玻璃瓶裝、真空包裝、即食精力湯等不同形式，讓老媽媽的辛勞長效封存，食用更方便，甘美更長年！\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151026154016.jpg', '苗栗縣', '公館鄉', 24.5046407, 120.827364),
(203, '1. 甘薯湯圓、2. 甘薯筍 (菜) 包', '苗栗縣西湖鄉三湖村4鄰71號', '苗栗縣西湖鄉是一連十二年獲得台灣環保評鑑第一名的好地方，全鄉好山好水，鄉民世代疼惜這片土地，用最傳統的有機耕作，鄉內不見農藥行的蹤跡，清澈溪流處處可見魚、蝦。經過一百二十天的細心呵護，在這美麗環保鄉中孕育成長的蕃薯是人人渴望追求的。甘藷湯圓以番薯及澱粉為主要材料，烹煮時不須解凍，將水煮開，直接放入即可，甜醎均宜。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151026154228.jpg', '苗栗縣', '西湖鄉', 24.5506389, 120.810083),
(204, '待客以鱒-醃鱒', '苗栗縣南庄鄉東村中正路195號', '鱒魚，魚中貴族，肉質細嫩，甜美多汁，無腥味，無小骨刺，不需刮鱗片，含有豐富蛋白質，低脂肪且低膽固醇。本產品係新鮮鱒魚經挑選醃製真空包裝，急速冷凍而成，可煎、可烤、可炸、再加點新鮮檸檬汁 美味無窮是過年、過節、最佳伴手禮。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151026154300.jpg', '苗栗縣', '南庄鄉', 24.6055422, 121.001136),
(205, '菜乾禮盒組', '苗栗縣三義鄉廣盛村廣盛9-27號', '採用有機自然生態農耕方式栽種，讓蘿蔔、高麗菜、芥菜等在土壤中吸收豐富之礦物質、微量元素後，經過天然發酵與高能量陽光日曬製成，不用烹煮光是空氣中就可以聞到一股誘人的菜乾香瀰漫，這懷念不已的好味道，不管送禮自用兩相宜。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151026154656.jpg', '苗栗縣', '三義鄉', 24.406434, 120.751328),
(206, '藺草禮盒組藺草手工藝品-草蓆、草帽', '苗栗縣苑裡鎮為公路65號', '苑裡三角藺草是一種工藝草，具有吸濕脫臭功效，特別設計的胸針及名片盒禮盒，手工精緻並帶有淡淡藺草香，是送禮的最佳選擇。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151026155303.jpg', '苗栗縣', '苑裡鎮', 24.4426396, 120.65256),
(207, '飛牛烤布丁禮盒', '苗栗縣通霄鎮南和里166號', '採用牧場高優質鮮乳，加入了雞蛋、精選乳酪、焦糖，加上天然珍貴的香草棒莢，經長時間細火烘培出紮實綿密的口感，嚴謹的製作，入口立刻可以感受到其中鮮乳的細緻滑嫩，香濃牛奶搭配著牧場自然土雞蛋，混合著柔軟的香草棒莢顆粒提味，提升入口時的感動，奶蛋香再搭配上焦糖淡淡芬芳，漸漸地在口中融合一起，讓您品嚐的每一口都是營養、健康、美味。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151026155336.jpg', '苗栗縣', '通霄鎮', 24.4422344, 120.740641),
(208, '飛牛牧場原野-酪農餅乾', '苗栗縣通霄鎮南和里166號', '採用牧場高優質100鮮乳，並以自產的健康土雞蛋，長時間細火烘焙出紮實綿密的口感，入口後立即感受鮮乳的香濃，讓您品嚐的每一口都是營養又香純的美味。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151026155750.jpg', '苗栗縣', '通霄鎮', 24.4422344, 120.740641),
(209, '飛牛甜心蛋糕', '苗栗縣通霄鎮南和里166號', '甜心蛋糕嚴謹的製作，使用手工飛牛優格加上天然珍貴的香草提味，經長時間細火烘培，入口立刻享受紮實綿密的感動。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151026160558.jpg', '苗栗縣', '通霄鎮', 24.4422344, 120.740641),
(210, '百變薑餅人-提著幸福去旅行', '苗栗縣大湖鄉栗林村薑麻園9鄰6號', '位於苗栗縣大湖鄉的薑麻園，海拔 500 至 800 多公尺，由於地形、土質、及天然氣候，其所生產的生薑的品質及數量，廣受各界讚譽。因此運用當地特產製成造型可愛的薑餅人，搖身一變成為旅途中隨手拾得的幸福，適合您與家人朋友一同分享。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151026160657.jpg', '苗栗縣', '大湖鄉', 24.3804378, 120.832666),
(211, '福桂滿堂禮', '苗栗縣獅潭鄉新店村125號', '獅潭鄉農會的桂竹筍，主要採擷取嫩筍的部分，成長過程完全不受農藥污染，是大自然賜與的天然食材，而福菜是挑選品質優良的大芥菜，不添加防腐劑與色素，滋味非常鮮美，酸酸甘甘的味道，非常爽口，是您佳節送禮的最佳選擇。\r\n', 'https://ezgo.ardswc.gov.tw/Uploads/opendata/BuyItem/APPLY_D/20151026161023.jpg', '苗栗縣', '獅潭鄉', 24.5409581, 120.921605);
